import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import axios from 'axios';
import { Baseurl } from '../baseurl';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Settings = () => {
    return (
        <>
            <div class="page-content page-container mx-auto mb-5" id="page-content" style={{ marginTop: '80px' }}>
                <small><ToastContainer /></small>
                <div class="row container d-flex justify-content-center mx-auto">
                    <div class="profile-card user-card-full">
                        <div class="col-md-8 mx-auto">
                            <div class="card-block">
                                <h6 class="m-b-20 p-b-5 b-b-default f-w-600">Basic Information</h6>
                                <div class="row">
                                    <Formik
                                        initialValues={{ phone: '234' }}
                                        validate={values => {
                                            const errors = {};
                                            if (!values.phone) {
                                                errors.phone = '*Phone Number Required';
                                            } else if (Math.ceil(Math.log10(values.phone)) < 13) {
                                                errors.phone = '*Phone Number Should be 11 characters';
                                            } else if (Math.ceil(Math.log10(values.phone)) > 13) {
                                                errors.phone = '*Phone Number Should be 11 characters';
                                            }

                                            return errors;
                                        }}
                                        onSubmit={(values, { setSubmitting }) => {
                                            setTimeout(() => {
                                                let token = sessionStorage.Token
                                                axios.post(`${Baseurl}updateprofile`, values, {
                                                    headers: {
                                                        'Authorization': 'Bearer' + token
                                                    }
                                                }).then((res) => {
                                                    setSubmitting(false)
                                                    if (res.data === 'phoneexists') {
                                                        toast(`😔 You have a number already`, {
                                                            autoClose: 2000,
                                                        })
                                                    } else if (res.data === 'updated') {
                                                        toast(`👍 Successfully Updated`, {
                                                            autoClose: 2000,
                                                        })
                                                    }
                                                }).catch((error) => {
                                                    if (error.response.status === 422) {
                                                        setSubmitting(false);
                                                        toast("✋ Phone Number already taken!", {
                                                            autoClose: 2000,
                                                        })
                                                    }
                                                })

                                            }, 400);
                                        }}
                                    >
                                        {({ isSubmitting }) => (

                                            <Form>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Phone Number</span> </div>
                                                    <div class="row row-2"> <Field type="number" placeholder="Phone Number" name="phone" />
                                                        <ErrorMessage name="phone" component="small" className='text-danger' /> </div>
                                                </div>
                                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Update</b></button>
                                            </Form>
                                        )}
                                    </Formik>
                                </div>
                                <h6 class="m-b-20 m-t-40 p-b-5 b-b-default f-w-600">Bank Details</h6>
                                <div class="row">
                                    <Formik
                                        initialValues={{ accName: '', accNo: '', bankName: '' }}
                                        validate={values => {
                                            const errors = {};
                                            if (!values.bankName) {
                                                errors.bankName = '*Bank Name Required';
                                            }
                                            else if (!values.accName) {
                                                errors.accName = '*Account Name Required';
                                            } else if (!values.accNo) {
                                                errors.accNo = '*Account Number Required';
                                            }

                                            return errors;
                                        }}
                                        onSubmit={(values, { setSubmitting }) => {
                                            setTimeout(() => {


                                            }, 400);
                                        }}
                                    >
                                        {({ isSubmitting }) => (

                                            <Form>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Bank Name</span> </div>
                                                    <div class="row row-2"> <Field type="text" placeholder="Your Bank Name" name="bankName" />
                                                        <ErrorMessage name="bankName" component="small" className='text-danger' /> </div>
                                                </div>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Account Name</span> </div>
                                                    <div class="row row-2"> <Field type="text" placeholder="Your Account Name" name="accName" />
                                                        <ErrorMessage name="accName" component="small" className='text-danger' /> </div>
                                                </div>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Account Number</span> </div>
                                                    <div class="row row-2"> <Field type="number" placeholder="Your Account Number" name="accNo" />
                                                        <ErrorMessage name="accNo" component="small" className='text-danger' /> </div>
                                                </div>
                                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Add Bank</b></button>
                                            </Form>
                                        )}
                                    </Formik>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


        </>
    )
}

export default Settings
