import React, { useState } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Baseurl } from '../../baseurl';

const BasicDetails = () => {
    const [showloader, setShowloader] = useState(false)
    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-md-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />

                            <small><strong>📚  Create a Course</strong></small><br />
                            <small><strong>Stage 1:</strong> Course Basic Details</small>
                            <Formik
                                initialValues={{category: '', title: '',  cost:'', desc:'' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.category) {
                                        errors.category = '*Category Required';
                                    }
                                    else if (!values.title) {
                                        errors.title = '*Course Titile Required';
                                    }
                                    else if (values.cost) {
                                        errors.cost = '*Purpose Required';
                                    }



                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
            
                                    setTimeout(() => {
                                        
                                        // setShowloader(true)
                                        // if (sessionStorage.Token) {
                                        //     let token = sessionStorage.Token
                                        //     values.token = token
                                        //     axios.post(`${Baseurl}beatutor`, values).then((res) => {
                                        //         if(res.data === 'alreadyActivated'){
                                        //             setShowloader(false)
                                        //         toast("😔 Tutor portal already created!", {
                                        //             autoClose: 2000,
                                        //         })
                                        //         setTimeout(() => history.push('/tutor-portal'), 2000)
                                        //         }else if(res.data === 'tutorActivated'){
                                        //             setShowloader(false)
                                        //             toast("👍 Tutor portal  created!", {
                                        //                 autoClose: 2000,
                                        //             })
                                        //         }
                                        //     }).catch((error)=>{
                                        //       if(error.response.status){
                                        //           setShowloader(false)
                                        //         toast("😔 You need to be logged in!", {
                                        //             autoClose: 2000,
                                        //         })
                                        //         setTimeout(() => history.push('/login'), 2000)
                                        //         sessionStorage.setItem('Path', path.pathname)

                                        //       }
                                        //     })
                                        // } else {
                                        //     setShowloader(false)
                                        //     toast("😔 You need to be logged in!", {
                                        //         autoClose: 2000,
                                        //     })
                                        //     setTimeout(() => history.push('/login'), 2000)
                                        //     sessionStorage.setItem('Path', path.pathname)
                                        // }
                                        setSubmitting(false);
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Category</span> </div>
                                            <div class="row row-2">
                                                <Field as="select" name="category" class="form-select form-select-sm mt-1" aria-label=".form-select-sm example">
                                                    <option selected>Choose a category</option>
                                                    <option value="Design">Design</option>
                                                    <option value="Marketing">Marketing</option>
                                                    <option value="IT and Software">IT and Software</option>
                                                    <option value="Photograpy">Photograpy</option>
                                                    <option value="Engineering">Engineering</option>
                                                    <option value="Engineering">Engineering</option>
                                                    <option value="Agriculture">Agriculture</option>
                                                    <option value="Business">Business</option>
                                                </Field>
                                                <ErrorMessage name="category" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Course Title</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Course Title" name="title" />
                                                <ErrorMessage name="title" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Course Cost</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="Course Cost" name="cost" />
                                                <ErrorMessage name="cost" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Description</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Course Description" name="desc" />
                                                <ErrorMessage name="desc" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Save and Proceed</b></button>
                                    </Form>
                                )}
                            </Formik>

                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default BasicDetails
