import React from 'react'
import { useEffect } from 'react'
import { useLocation } from 'react-router'

const ScrollToTop = ({children}) => {
    const {pathname} = useLocation();
    useEffect(() => {
        window.scrollTo(0, 0);
    }, [[pathname]])
    return children || null;
}

export default ScrollToTop
