import React, {useEffect} from 'react'
import { Link as Scroll } from 'react-scroll'
const BackTotop = () => {
    return (
       
        <>
            <Scroll to='hero'><a href="" class="back-to-top d-flex align-items-center justify-content-center"><i class="bi bi-arrow-up-short"></i></a>
            </Scroll> 
        </>
    )
}

export default BackTotop 
let preloader = select('#preloader');
if (preloader) {
  window.addEventListener('load', () => {
    preloader.remove()
  });
}
