import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Skeleton from 'react-loading-skeleton';
import { Link } from 'react-router-dom';
import { Baseurl } from '../../baseurl';
import { useLocation } from 'react-router';



const MyCourses = () => {
    let path = useLocation()
    const [details, setDetails] = useState([])
    const [isFetching, setFecthing] = useState(false)
    useEffect(() => {
        setFecthing(true)
        let token = sessionStorage.Token
        axios.get(`${Baseurl}purchasedcourse`, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            setFecthing(false)
            if (res.data) {
                setDetails(res.data)
            }
        }).catch((error) => {
            setFecthing(false)
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })
    }, [])

    return (
        <>
            <section className="about container-fluid mt-4 mx-auto" id="about">
                <div class="row">
                    {details.length ? <small style={{ color: '#0D826E' }}><strong>Purhcased Courses</strong></small> : ''}
                    {isFetching ? <div className='row'>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                </div> :<> 
                {details.length ? details.map((item, index) => {
                        return (
                            <>
                                <div class="col-md-4 mt-2" data-aos="fade-in" data-aos-delay="100">
                                    <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                                        <div class="d-flex justify-content-between">
                                            <div class="d-flex flex-row align-items-center">
                                                <div class="icon">  <i class="fab fa-html5 fa-2x" style={{ color: '#ffbb2c' }}></i> </div>
                                            </div>
                                            <div class="badge"> <span style={{ color: '#FFFFFF', backgroundColor: '#F75B00' }}>Bought</span> </div>
                                        </div>
                                        <div class="mt-2">
                                            <small class="heading"><strong>{item.course_title}</strong></small>
                                            <div class="mt-2">
                                                <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i>
                                                <div class="mt-3"> <span class="text2"><strong>Preview</strong></span> </div>  </div>
                                        </div>
                                    </div>
                                </div>
                            </>
                        )
                    }) : <div className='col-md-6 col-sm mx-auto text-center mt-4'>
                    <h5>Your have no Course</h5>
                    <i class="fas fa-books text-danger fa-3x"></i>
                    <Link to='/available-courses'><button class="btn d-flex mx-auto" type="submit" ><b>Buy a Course</b></button></Link>
                    </div>
}
                </>} 
                </div>

            </section>
        </>
    )
}

export default MyCourses
