import React, { useState, useEffect} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useHistory } from 'react-router';
import axios from 'axios';
import { Baseurl } from '../../baseurl';
import { useParams } from 'react-router'


const  ManageCourse = () => {
    const history = useHistory()
    const { code } = useParams();
    const [isSubmitting, setSubmitting] = useState(false)
    const [details, setDetails] = useState({})
    const [newDetails, setNewDetails] = useState({category:'', title:'', cost:'', desc:''})

   useEffect(() => {
    axios.get(`${Baseurl}coursedetails`, {
        params: { code: code }
    }).then((res) => {
        if (res.data) {
            setDetails(res.data.details)
            // setSections(res.data.sections)
            // setLname(res.data.lname)
            // setNumber(res.data.no)
        }
    })
   }, [])

   const handleEdit = (e)=>{
       let defaultvalue = e.target.defaultValue;
       let value = e.target.value
       let name = e.target.name;
       if(value){
        setNewDetails({...newDetails, [name]:value})
       }else{
        setNewDetails({...newDetails, [name]:value})
       }

   }
   const editDetails = (e) =>{
    e.preventDefault();
    console.log(newDetails)
    // if(newDetails.course_title && newDetails.course_cost && newDetails.desc){
    //  console.log(newDetails)
    // }else{
    //     console.log(details)
    // }
   }

    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-md-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <p className='mt-2'>📚 Course Manager</p>
                            <small><strong><i class="fad fa-edit"></i>*Edit course basic details</strong></small>
                            
                                    <form onSubmit={editDetails}>
                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Category</span> </div>
                                            <div class="row row-2">
                                                <select name="category" class="form-select form-select-sm mt-1" aria-label=".form-select-sm example"  value={details.category} onChange={handleEdit}>
                                                    <option selected>Choose a category</option>
                                                    <option value="Design">Design</option>
                                                    <option value="Marketing">Marketing</option>
                                                    <option value="IT and Software">IT and Software</option>
                                                    <option value="Photography">Photography</option>
                                                    <option value="Engineering">Engineering</option>
                                                    <option value="Agriculture">Agriculture</option>
                                                    <option value="Cryptocurrency">Cryptocurrency</option>
                                                </select>
                                                {/* <ErrorMessage name="category" component="small" className='text-danger' /> */}
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Course Title</span> </div>
                                            <div class="row row-2">
                                                 <input type="text" placeholder="Course Title" name="title" defaultValue={details.course_title} onChange={handleEdit}/>
                                               
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Course Cost</span> </div>
                                            <div class="row row-2"> <input type="number" placeholder="Course Cost" name="cost" defaultValue={details.course_cost} onChange={handleEdit}/>
                                                <small style={{ color: '#0D826E' }}><strong>*Leave blank if not for profit</strong></small>
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Description</span> </div>
                                            <div class="row row-2">
                                                <input  placeholder="Course Description" name="desc"  defaultValue={details.description} onChange={handleEdit}/>
                                                {/* <ErrorMessage name="desc" component="small" className='text-danger' /> */}
                                            </div>
                                        </div>

                                        <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''}<small> Update Details</small></b></button>
                                    
                                    </form>

                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default ManageCourse
