import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import ScriptTag from 'react-script-tag';
import { useLocation } from 'react-router';
import {Link as Scroll } from 'react-scroll'
import { mainFunction } from '../main';
import { useHistory } from 'react-router';


const Navbar = ({ home, login, register, dashboard, BeTutor, tutorHome, tutorActivated, tutorPortal}) => {
    const [showDashboard, setShowDashboard] = useState(true);

    useEffect(() => {
        mainFunction();
    }, [])
    return (
        <>
            {/* <ScriptTag isHydrating={false} type="text/javascript" src="main.js" /> */}

            {/* <!-- ======= Header ======= --> */}
            <header id="header" class="fixed-top">
                <div class="container d-flex align-items-center">

                    <h1 class="logo me-auto"><Link to='/'>Fromhome<span style={{ color: '#05A081', fontSize: '25px' }}>.</span></Link></h1>
                    <nav id="navbar" class="navbar order-last order-lg-0" >
                        <ul>
                           <li><Link to='/' style={{ cursor: 'pointer' }}>Home</Link></li>
                            {sessionStorage.Token && !dashboard ? <Link to='/dashboard'>Dashboard</Link> :  ''}
                            {tutorActivated === 'true' ? <Link to='/tutor-portal'>Tutors' Portal</Link> : ''}
                            {tutorPortal ? <Link to='/tutor-portal'>Create a Course</Link> : ''}
                            {tutorPortal ? <Link to='/tutor-portal'>Created Courses</Link>:''}
                            {tutorPortal ? <Link to='/tutor-portal'>Payment</Link> : ''}
                            {home ? <Scroll to='about' style={{ cursor: 'pointer' }}>About</Scroll> : ''}
                            <Link to='/available-courses'>Available Courses</Link>
                            {dashboard ? <a href='/dashboard/my-courses'>My Courses</a> : ''}
                            {dashboard ? <a href='/dashboard/transactions'>Transactions</a> : ''}
                            {dashboard ? <a href='/dashboard/wishlists'>Wishlists</a> : ''}
                            {dashboard ? <a href='/dashboard/myprofile'>Profile</a> : ''}
                            {home || register || BeTutor ? <Link to='/login'>Login</Link> : ''}
                            {home || login ? <Link to='/register'>Register</Link> : ''}
                            <li><Scroll to='footer' style={{ cursor: 'pointer' }}>Contact</Scroll></li>
                        </ul>
                        <i class="bi bi-list mobile-nav-toggle"></i>
                    </nav>
                    {/* <!-- .navbar --> */}

                    {!BeTutor  && !tutorPortal ?  <Link to='/tutor' href="courses.html" class="get-started-btn">Be a Tutor <i class="fal fa-long-arrow-right"></i></Link> : ''}
                    <Link to='/cart' className='ml-2 chart'><strong><i class="fad fa-shopping-cart"></i></strong></Link>
                </div>
            </header>
            {/* <!-- End Header --> */}


        </>
    )
}

export default Navbar
