import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Baseurl } from '../../baseurl';
import { useLocation } from 'react-router';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Skeleton from 'react-loading-skeleton';
import { Link } from 'react-router-dom';

const SectionFiles = () => {
    const path = useLocation();
    const [section, setSection] = useState([])
    const [showloader, setShowloader] = useState(false)

    useEffect(() => {
        let Coursecode = localStorage.code;
        let token = sessionStorage.Token
        if(token){
            axios.get(`${Baseurl}getsections`, {
                params: {
                    code: Coursecode,
                    token: token
                }
            }).then((res) => {
                if (res.data) {
                    setSection(res.data)
                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        }else{

        }

    }, [])
    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-lg-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />


                            <p>ðŸ”—<strong>Stage 3:</strong> Section Files</p>
                            <small><strong>*Add at least a file to each section</strong></small>
                            <Formik
                                initialValues={{ section: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.section) {
                                        errors.section = '*Section name is required';
                                    }
                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {

                                 

                                    setSubmitting(false);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        {section.length ? <div class="row-1">
                                            <div className='row'>

                                                {section.map((item, index) => {
                                                    return (

                                                        <div className='col-md-6 col-sm-5 mx-auto py-2 text-center'>
                                                            <small><strong> Section {index + 1}</strong></small>
                                                            <div className='mb-2 py-1 text-light section-loop'>
                                                                <small className='text-capitalize'>{item.section_name}</small>
                                                            </div>
                                                        </div>

                                                    )
                                                })}


                                            </div>

                                        </div> : <div class="row-1">
                                        <div className='col-md-6 col-sm-5 mx-auto py-2 text-center'>
                                        <Skeleton count={2}/>   
                                        </div>
                                            </div>}

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Section Names</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Type in section names" name="section" />
                                                <ErrorMessage name="section" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square fa-2x" style={{ color: '#071B4D' }}></i><small><strong> Click to Add</strong></small></button>
                                    </Form>
                                )}
                            </Formik>

                            <button class="btn d-flex mx-auto mb-2" type="submit" ><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Upload Files</b></button>
                            <Link style={{ cursor: 'pointer' }}><strong><i class="fal fa-long-arrow-left" style={{ color: '#0D826E' }}></i> Click to preview course</strong></Link>
                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default SectionFiles
