import React, {useState, useEffect} from 'react'
import { Link as Link2, useHistory } from 'react-router-dom';
import { Link } from 'react-scroll';
import Navbar from './navbar';
import Footer from './footer'
import { Baseurl } from '../baseurl';
import axios from 'axios';
// import Swal from 'sweetalert2'
// import axios from 'axios';
// import {Baseurl} from '../components/Baseurl.js';

// const Toast = Swal.mixin({
//   toast: true,
//   position: 'top-end',
//   showConfirmButton: false,
//   timer: 500,
//   timerProgressBar: true,
//   didOpen: (toast) => {
//   toast.addEventListener('mouseenter', Swal.stopTimer)
//   toast.addEventListener('mouseleave', Swal.resumeTimer)
//   }
// })

// const Toast2 = Swal.mixin({
//   toast: true,
//   position: 'top-end',
//   showConfirmButton: false,
//   timer: 1500,
//   didOpen: (toast) => {
//   toast.addEventListener('mouseenter', Swal.stopTimer)
//   toast.addEventListener('mouseleave', Swal.resumeTimer)
//   }
// })


const Login = () => {
useEffect(() => {
  localStorage.removeItem('Email');

},[]);
let history = useHistory();
 const [isOpen, setIsOpen] = useState(false)
 const [value, setValue] = useState('Type your email');
 const [value2, setValue2] = useState('Type your password');
 const [isShow, setIsShow] = useState(false);
 const [isShow2, setIsShow2] = useState(false);
 const [login, setLogin] = useState({email: '', password: '' });
 const [isRoll, setIsRoll] = useState(false)

  const toggle = () => {
   setIsOpen(!isOpen);
  };
 
 const handleClick1 = () => {
  setValue('');
  setIsShow(true) 
 }
 const handleClick2 = () => {
  setValue2('');
  setIsShow2(true) 
 }

const handleChange = (e) => {
  let name = e.target.name
  let value = e.target.value
  setLogin({...login, [name]:value})
}
const submitForm = (e) => {
  setIsRoll(true)
  e.preventDefault();
  if (login.email && login.password){
    axios.post(`${Baseurl}login.php`, JSON.stringify(login)).then(res => {
      setIsRoll(false)
      let loginResponse = res.data;
      if (loginResponse.LoginSuccess) {
      // Toast.fire({
      // icon: 'success',
      // title: 'Logged in successfully'
      // })
      localStorage.setItem('Token', loginResponse.Auth);
      localStorage.removeItem('details');
      setTimeout(() => history.push("/dashboard"), 500);
      } else if(loginResponse.IncorrectPwd) {
        // Toast2.fire({
        //   icon: 'error',
        //   title: 'Incorrect password'
        // })
      } else if (loginResponse.EmailNotFound) {
        // Toast2.fire({
        //   icon: 'info',
        //   title: 'Email not found'
        // })
      }
    })
    
  } else {
    setIsRoll(false)
    // Toast2.fire({
    //   icon: 'info',
    //   title: 'Please fill all input'
    // })
  }
 
}
 
 return (
  <>
   <Navbar/>
   <section  className="about container-fluid mt-4" id="about">
     <div className="row">
      <div className="col-lg-5  d-lg-block order-2 order-lg-1" data-aos="fade-left" data-aos-delay="100">
      <img src="assets/img/student2.jpg" className="img-fluid login-img" alt="" style={{borderRadius:'10px'}}/>
     
 </div>

     <div className="col-lg-6 order-1 order-lg-2" data-aos="fade-left" data-aos-delay="100">
     <div class="section-title">
      <h2>LOGIN</h2>
      </div>
      {/* <form onSubmit={submitForm}>
           {isShow ? <label className="ml-2" data-aos="fade" style={{fontFamily: '"Raleway" sans-serif', color:'#5fcf80'}}><small>Type your email</small></label>
    : null }
       <div class="input-group mb-3">
      <div class="input-group-prepend">
      <span class="input-group-text border-top-0 border-left-0 border-right-0 rounded-0 border-bottom-0 " id="basic-addon1"><i class="fas fa-envelope fa-2x"></i></span>
      </div>
      <input type="text" class="form-control border-top-0 border-left-0 border-right-0 rounded-0 py-3" placeholder={value} aria-label="Username" aria-describedby="basic-addon1" onClick={handleClick1} name="email" onChange={handleChange} value={login.email}/>
      </div><br/>
      {isShow2 ? <label className="ml-2" data-aos="fade" style={{fontFamily: '"Raleway" sans-serif', color:'#5fcf80'}}><small>Type your password</small></label>
   
    : null }
       <div class="input-group mb-3">
      <div class="input-group-prepend">
      <span class="input-group-text border-top-0 border-left-0 border-right-0 rounded-0 border-bottom-0 " id="basic-addon1"><i class="fas fa-lock fa-2x"></i></span>
      </div>
      <input type="password" class="form-control border-top-0 border-left-0 border-right-0 rounded-0 py-3" placeholder={value2} aria-label="Username" aria-describedby="basic-addon1" onClick={handleClick2} name="password" onChange={handleChange} value={login.password}/>
     </div>
     <div className="text-center">
     <button className="btn g py-2 px-4 shadow font-weight-bold">{isRoll ? <i class="fad fa-spinner fa-spin"></i> 
    : null } LOGIN</button>
       </div>
    </form> */}
     
     <div class="login-card mt-50 mb-50">
    <div class="login-card-title mx-auto"> Settings </div>
    <div class="nav">
        <ul class="mx-auto">
            <li><a href="#">Account</a></li>
            <li class="active"><a href="#">Payment</a></li>
        </ul>
    </div>
    <form> <span id="login-card-header">Saved login-cards:</span>
        <div class="row row-1">
            <div class="col-2"><img class="img-fluid" src="https://img.icons8.com/color/48/000000/masterlogin-card-logo.png" /></div>
            <div class="col-7"> <input type="text" placeholder="**** **** **** 3193"/> </div>
            <div class="col-3 d-flex justify-content-center"> <a href="#">Remove login-card</a> </div>
        </div>
        <div class="row row-1">
            <div class="col-2"><img class="img-fluid" src="https://img.icons8.com/color/48/000000/visa.png" /></div>
            <div class="col-7"> <input type="text" placeholder="**** **** **** 4296"/> </div>
            <div class="col-3 d-flex justify-content-center"> <a href="#">Remove login-card</a> </div>
        </div> <span id="login-card-header">Add new login-card:</span>
        <div class="row-1">
            <div class="row row-2"> <span id="login-card-inner">login-card holder name</span> </div>
            <div class="row row-2"> <input type="text" placeholder="Bojan Viner"/> </div>
        </div>
        <div class="row three">
            <div class="col-7">
                <div class="row-1">
                    <div class="row row-2"> <span id="login-card-inner">login-card number</span> </div>
                    <div class="row row-2"> <input type="text" placeholder="5134-5264-4"/> </div>
                </div>
            </div>
            <div class="col-2"> <input type="text" placeholder="Exp. date"/> </div>
            <div class="col-2"> <input type="text" placeholder="CVV"/> </div>
        </div> <button class="btn d-flex mx-auto"><b>Add login-card</b></button>
    </form>
</div>
     </div>
    
    </div>
    </section>
  
    
    
    <Footer/>
   </>
);
};

export default Login;
