import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Baseurl } from '../../baseurl';
import { useLocation } from 'react-router';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Skeleton from 'react-loading-skeleton';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router';
import { useRef } from 'react';

const SectionFiles = () => {
    const history = useHistory()
    const path = useLocation();
    const [section, setSection] = useState([])
    const [fileName, setFileName] = useState('')
    const sectionName = useRef();
   
    const [showloader, setShowloader] = useState(false)

    useEffect(() => {
        let Coursecode = localStorage.code;
        let token = sessionStorage.Token
        if (token && Coursecode) {
            axios.get(`${Baseurl}getsections`, {
                params: {
                    code: Coursecode,
                    token: token
                }
            }).then((res) => {
                if (res.data) {
                    setSection(res.data)
                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        } else {
            history.push('/dashboard/created-courses');
        }

    }, [])

    const handleFile = (e) => {
        setFileName(e.target.files[0])
        
    }


    const addFile = (e) =>{
    e.preventDefault();
    let value = {file:'', section:''}
    value.section = sectionName.current.value
    let formData = new FormData();
    formData.append('courseFile', fileName)
    axios.post(`${Baseurl}uploadfiles`, formData).then((res)=>{
    console.log(res.data)
    })
    
     
    }
    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-lg-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />


                            <p>🔗<strong>Stage 3:</strong> Section Files</p>
                            <small><strong>*Add at least a file to each section</strong></small>
                            <form></form>

                            <form onSubmit={addFile}>
                              
                                <div class="row-1">
                                    <div class="row row-2"> <span id="login-card-inner">Section Files</span> </div>
                                    <div class="row row-2">
                                        <input class="form-control form-control-sm mt-2" id="formFileSm" type="file" name="file"
                                            onChange={ handleFile }/>
                                        {/* <ErrorMessage name="file" component="small" className='text-danger'/> */}
                                    </div>
                                </div>
                                <div class="row-1">
                                    <div class="row row-2"> <span id="login-card-inner">Attach file to sections</span> </div>
                                    <div class="row row-2">
                                        <select name="section" class="form-select form-select-sm mt-1" aria-label=".form-select-sm example" ref={sectionName}>
                                            <option selected>This file belongs to?</option>
                                            {section.map((item, index) => {
                                                return (
                                                    <option className='text-capitalize' value={item.section_name} key={index}>{item.section_name}</option>
                                                )
                                            })}

                                        </select>
                                        {/* <ErrorMessage name="section" component="small" className='text-danger' /> */}
                                    </div>
                                </div>

                                <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square fa-2x" style={{ color: '#071B4D' }}></i><small><strong> Click to Add</strong></small></button>

                            </form>

