                            <Link style={{ cursor: 'pointer' }}><strong> Click to preview course <i class="fal fa-long-arrow-right" style={{ color: '#0D826E' }}></i></strong></Link>
                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default SectionFiles
