import React, {useEffect} from 'react'
import { Link } from 'react-router-dom'
import Footer from '../landing_page/footer'
import Navbar from '../landing_page/navbar'
import { BrowserRouter as Route, Switch } from 'react-router-dom';
import Home from './home';
import MyCourses from './my_courses';
import Wishlists from './wishlists';
import Transactions from './transactions';
import { useLocation } from 'react-router';
import axios from 'axios';
import { Baseurl } from '../baseurl';
import { useDispatch } from 'react-redux';
import { isLogged } from '../redux/actions';
import { useState } from 'react';

const Dashboard = () => {
 const [profile, setProfile] = useState({});
 const dispatch = useDispatch();
 useEffect(() => {
     let token = sessionStorage.Token
     if(token){
     axios.post(`${Baseurl}auth/me`, {token:token}).then((res)=>{
        if(res.data){
        let data = res.data
        dispatch(isLogged(true))
        setProfile(data)
        }
      
     }).catch((error)=>{
        if(error.response.status === 401){
        dispatch(isLogged(false))
        }
     })
     }else{
      dispatch(isLogged(false))  
     }
 }, [])
    let path = useLocation()
    return (
        <>

        <Navbar dashboard={path.pathname} tutorActivated={profile.tutor_portal}/>
            <Switch>
                <Route exact path='/dashboard'>
                    <Home firstname={profile.firstname}/>
                </Route>

                <Route exact path='/dashboard/my-courses'>
                    <MyCourses/>
                </Route>

                <Route  exact path='/dashboard/wishlists'>
                    <Wishlists/>
                </Route>

                <Route exacct  path='/dashboard/transactions'>
                    <Transactions/>
                </Route>

            </Switch>
            {/* dashboard section Ends */} 
            <Footer/> 
       
      
        </>
    )
}

export default Dashboard
