import React from 'react'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'
import { Formik, Form, Field, ErrorMessage } from 'formik';

const Settings = ({ profile }) => {
    return (
        <>
            <div class="page-content page-container mx-auto mb-5" id="page-content" style={{ marginTop: '100px' }}>
                <div class="row container d-flex justify-content-center mx-auto">
                    <div class="profile-card user-card-full">
                        <div class="col-md-8 mx-auto">
                            <div class="card-block">
                                <h6 class="m-b-20 p-b-5 b-b-default f-w-600">Basic Information</h6>
                                <div class="row">
                                    <Formik
                                        initialValues={{phone: ''}}
                                        validate={values => {
                                            const errors = {};
                                            if (!values.phone) {
                                                errors.phone = '*Phone Number Required';
                                            }

                                            return errors;
                                        }}
                                        onSubmit={(values, { setSubmitting }) => {
                                            setTimeout(() => {


                                            }, 400);
                                        }}
                                    >
                                        {({ isSubmitting }) => (

                                            <Form>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                                                    <div class="row row-2"> <Field type="number" placeholder="Phone Number" name="phone" />
                                                        <ErrorMessage name="phone" component="small" className='text-danger' /> </div>
                                                </div>
                                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Update</b></button>
                                            </Form>
                                        )}
                                    </Formik>
                                </div>
                                <h6 class="m-b-20 m-t-40 p-b-5 b-b-default f-w-600">Bank Details</h6>
                                <div class="row">
                                    <Formik
                                        initialValues={{accName: '', accNo: '', bankName:''}}
                                        validate={values => {
                                            const errors = {};
                                            if (!values.name) {
                                                errors.email = '*Email Required';
                                            } else if (
                                                !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                                            ) {
                                                errors.email = 'Invalid email address';
                                            } else if (!values.password) {
                                                errors.password = '*Password Required';
                                            }

                                            return errors;
                                        }}
                                        onSubmit={(values, { setSubmitting }) => {
                                            setTimeout(() => {


                                            }, 400);
                                        }}
                                    >
                                        {({ isSubmitting }) => (

                                            <Form>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Bank Name</span> </div>
                                                    <div class="row row-2"> <Field type="text" placeholder="Your Bank Name" name="email" />
                                                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                                                </div>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Account Name</span> </div>
                                                    <div class="row row-2"> <Field type="text" placeholder="Your Account Name" name="email" />
                                                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                                                </div>
                                                <div class="row-1">
                                                    <div class="row row-2"> <span id="login-card-inner">Account Number</span> </div>
                                                    <div class="row row-2"> <Field type="number" placeholder="Your Account Number" name="email" />
                                                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                                                </div>
                                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Add Bank</b></button>
                                            </Form>
                                        )}
                                    </Formik>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


        </>
    )
}

export default Settings
