import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Baseurl } from '../../../baseurl';
import { useHistory } from 'react-router';
import { useLocation } from 'react-router';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router';



const ManageSections = () => {
    const { code } = useParams();
    const [section, setSection] = useState([])
    const [showloader, setShowloader] = useState(false)
    const [sectionVideo, setSectionVideo] = useState([])
    const [isFecthing, setIsFecthing] = useState(false)

    useEffect(() => {
        let section = JSON.parse(localStorage.getItem('sections'))
        setSection(section)
    }, [])


    const deleteSection = (id, index) => {
        axios.post(`${Baseurl}`).then((res)=>{
            console.log(res.data)
        })
        // const newSection = [...sectionVideo]
        // newSection.splice(i, 1);
        // setSectionVideo(newSection)
        // toast("ðŸ‘ Section Deleted!", {
        //     autoClose: 2000,
        // })

    }

    const editSection = (id, name) => {
        setIsFecthing(true)
        axios.get(`${Baseurl}sectiondetails`, {
            params: { id: id }
        }).then((res) => {
            setIsFecthing(false)
            setSectionVideo(res.data.videos)
        })
    }

    const uploadCourse = () => {
        // setShowloader(true)
        // let values = formikPersist.values
        // if (values) {
        //     if (values.category && values.desc && values.title && values.sections.length) {
        //         let token = sessionStorage.Token
        //         values.token = token
        //         axios.post(`${Baseurl}postcourse`, values).then((res) => {
        //             if (res.data) {
        //                 if (res.data === 'pending') {
        //                     setShowloader(false)
        //                     toast("âœ‹  You have a Pending Course, Please Add Files!", {
        //                         autoClose: 2000,
        //                     })
        //                     setTimeout(() => history.push('/dashboard/create-a-course/videos'), 2000)
        //                 } else {
        //                     setShowloader(false)
        //                     toast("ðŸ‘ Succesfully Uploaded, Add Course Videos!", {
        //                         autoClose: 2000,
        //                     })
        //                     localStorage.setItem('code', res.data)
        //                     localStorage.removeItem('course')
        //                     setTimeout(() => history.push('/dashboard/create-a-course/videos'), 2000)
        //                 }
        //             }
        //         }).catch((error) => {
        //             if (error) {
        //                 if (error.response.status === 500) {
        //                     setShowloader(false)
        //                     toast("âœ‹  Please Login to continue!", {
        //                         autoClose: 2000,
        //                     })
        //                     history.push('/login')
        //                     sessionStorage.setItem('Path', path.pathname)
        //                 }
        //             }
        //         })

        //     } else {
        //         setShowloader(false)
        //         toast("âœ‹  Please fill all basic course details!", {
        //             autoClose: 2000,
        //         })
        //         setTimeout(() => history.goBack(), 2000)
        //     }
        // } else {
        //     setShowloader(false)
        //     toast("âœ‹  No course found!", {
        //         autoClose: 2000,
        //     })
        // }
    }

    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-lg-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <p>ðŸ“š Course Manager</p>
                            <small><strong><i class="fad fa-edit" style={{ color: '#071B4D' }}></i>*Edit Course sections</strong></small>
                            <Formik
                                initialValues={{ section: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.section) {
                                        errors.section = '*Section name is required';
                                    }
                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    let newSection = { section_name: values.section }
                                    section.push(newSection)
                                    setSection(section)
                                    localStorage.setItem('sections', JSON.stringify(section))
                                    setSubmitting(false);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        {section.length ? <div class="row-1">
                                            <div className='row'>

                                                {section.map((item, index) => {
                                                    return (

                                                        <div className='col-md-6 col-sm-5 mx-auto py-2 text-center'>
                                                            <small><strong><i class="fas fa-edit text-danger" data-bs-toggle="modal" data-bs-target="#exampleModal" onClick={() => editSection(item.id, item.section_name)}></i> Edit Section {index + 1}</strong></small>
                                                            <div className='mb-2 py-1 text-light section-loop'>
                                                                <small className='text-capitalize'>{item.section_name} </small>
                                                            </div>
                                                        </div>
                                                    )
                                                })}
                                            </div>
                                        </div> : ''}
                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Section Names</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Type in section names" name="section" />
                                                <ErrorMessage name="section" component="small" className='text-danger' />
                                            </div>
                                        </div>
                                        <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square" style={{ color: '#071B4D' }}></i><small><strong> Click to Add New Section</strong></small></button>
                                    </Form>
                                )}
                            </Formik>

                            <button class="btn d-flex mx-auto" type="submit" onClick={uploadCourse}><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} <small>Update Section</small></b></button>
                            <small className='text-danger'>*Cross-check before you upload course</small><br />

                            <Link to={`/dashboard/created-courses/manage/edit-basic-details/${code}`} style={{ cursor: 'pointer' }}><strong><small><i class="fal fa-long-arrow-left" style={{ color: '#0D826E' }}></i> Edit Previous Stage </small></strong></Link>

                        </div>
                    </div>

                </div>
            </section>

            {/* Modal */}
            <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            {/* <p class="modal-title text-capitalize" id="exampleModalLabel"><strong>{sectionV.name}</strong></p> */}
                            <i class="fad fa-times-square" style={{ color: '#0D826E', fontSize: '25px' }} data-bs-dismiss="modal" aria-label="Close"></i>
                        </div>
                        <div class="modal-body">
                            {/* section name */}
                            <small><strong>Section Name</strong></small>
                            <form>
                                <div class="row-1">
                                    <div class="row row-2"> <span id="login-card-inner">Edit Section Name</span> </div>
                                    <div class="row row-2"> <input type="text" placeholder="Type new section name" name="section"  />
                                    </div>
                                </div>
                                <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square" style={{ color: '#071B4D' }}></i><small><strong> Click to Save</strong></small></button>

                            </form>
                            {/* //section videos */}
                            <div className='mt-4'>
                                <small><strong>Section Videos</strong></small>
                                <div className='mb-2 py-1 text-light section-loop text-center'>
                                    <small className='text-capitalize'>
                                        {isFecthing ? <i class="fas fa-spinner fa-spin"></i> : <>
                                            {sectionVideo.length ? sectionVideo.map((item, index) => {
                                                return (
                                                    <>
                                                    <small className='text-capitalize' key={index}><i class="fad fa-trash text-danger" onClick={()=>deleteSection(item.id, index)}></i> {item.video_name}</small>
                                                    </>
                                                )
                                            }) : <small>No Course Video</small>}</>}
                                    </small>
                                </div>
                                <form>
                                    <div class="row-1">
                                        <div class="row row-2"> <span id="login-card-inner">Add New Section Video</span> </div>
                                        <div class="row row-2">
                                            <input class="form-control form-control-sm mt-2" id="formFileSm" type="file"
                                            />
                                            {/* {showVideoError ? <small className='text-danger'>*Please choose a file</small> : ''} */}
                                        </div>
                                    </div>
                                    <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square" style={{ color: '#071B4D' }}></i><small><strong> Click to Add Video</strong></small></button>
                                </form>

                                {/* section files */}
                                <div className='mt-4'>
                                    <small><strong>Section Files</strong></small>
                                    <div className='mb-2 py-1 text-light section-loop text-center'>
                                        <small className='text-capitalize'><strong><i class="fad fa-trash text-danger"></i></strong> cccccccc </small>
                                    </div>
                                </div>
                                <form>
                                    <div class="row-1">
                                        <div class="row row-2"> <span id="login-card-inner">Add New Section File</span> </div>
                                        <div class="row row-2">
                                            <input class="form-control form-control-sm mt-2" id="formFileSm" type="file"
                                            />
                                            {/* {showVideoError ? <small className='text-danger'>*Please choose a file</small> : ''} */}
                                        </div>
                                    </div>
                                    <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square" style={{ color: '#071B4D' }}></i><small><strong> Click to Add File</strong></small></button>
                                </form>
                            </div>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn"><small>Delete Section</small></button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ManageSections
