import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import RelatedCourses from './related_courses'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'



const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [details, setDetails] = useState({})
    const [sections, setSections] = useState([])
    const [lname, setLname] = useState('')
    const [number, setNumber] = useState('')
    const [access, setAccess] = useState(false)

    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setDetails(res.data.details)
                setSections(res.data.sections)
                setLname(res.data.lname)
                setNumber(res.data.no)
            }
        })

    }, [])

    useEffect(() => {
        let token = sessionStorage.Token
        if (token) {
            axios.get(`${Baseurl}courseaccess`, {
                headers: {
                    'Authorization': 'Bearer' + token
                },
                params: {
                    code: code
                }
            }).then((res) => {
                if (res.data === 'paid') {
                    setAccess(true)
                }
            })
        }


    }, [])


    const addToCart = () => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            if(details.course_title || details.course_code || details.course_cost || details.category || lname){
                let courseDetails = {
                    title: details.course_title, code: details.course_code,
                    cost: details.course_cost, category: details.category, tutor:lname
                }
                cart.push(courseDetails);
                localStorage.setItem('cart', JSON.stringify(cart))
                toast("👍 Added to Cart!", {
                    autoClose: 2000,
                })
            }
        } else {
            let cart = [];
            let courseDetails = { courseCode: code }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        }
    }

    const addwishlist = () => {
        let token = sessionStorage.Token
        if (token) {
            axios.post(`${Baseurl}addwishlist`, { code: code }, {
                headers: {
                    'Authorization': 'Bearer' + token
                }
            }).then((res) => {
                if (res.data === 'added') {
                    toast("👍 Added to wishlists!", {
                        autoClose: 2000,
                    })
                } else if (res.data === 'alreadyAdded') {
                    toast("😔 Already Added!", {
                        autoClose: 2000,
                    })
                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        } else {
            toast("😔 Login to Wishlist!", {
                autoClose: 2000,
            })

        }
    }

    return (
        <>
            <Navbar />
            <section id="course-details" class="course-details">
                <small><ToastContainer /></small>
                <div class="container" data-aos="fade-in">
                    <div class="row">
                        <div class="col-lg-8">
                            <img src="assets/img/course-details.jpg" class="img-fluid" alt="" />
                            <h3>{details.course_title ? details.course_title : <Skeleton count={1} />}</h3>
                            <p>
                                {details.description ? details.description : <p><Skeleton count={4} /></p>}
                            </p>
                            <div className='mt-3'>
                                {access ? '' : <>{details.course_title ? <small onClick={addwishlist} style={{ color: '#071B4D', cursor: 'pointer' }} className='px-2'><i class="fas fa-bookmark"></i><strong> Wishlist</strong></small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</>}
                                {number ? <small style={{ color: '#071B4D' }} className='px-2'><i class="fad fa-user-alt"></i><strong> {number} Enrolled</strong></small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}
                                {details.course_title ? <small style={{ color: '#071B4D' }} className='px-2'><strong>
                                    <i class="fas fa-star" style={{ color: '#FFD700', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i> </strong>
                                </small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}<br />

                                <section id="cource-details-tabs" class="cource-details-tabs mt-5">
                                    <ul class="nav nav-tabs flex-column">
                                        <li class="nav-item">
                                            <a class="nav-link active show" data-toggle="tab" href="#tab-1">📚 Course Sections</a>
                                        </li>
                                    </ul>
                                   
                                              <div class="p-2 learn">
                                                <div class="row">
                                                {sections.length ? sections.map((item, index) => {
                                            return (
                                                <div class="col-md-6 p-2">
                                                 <small><i class="fa fa-check-circle me-2"></i>{}</small>
                                                </div>
                                              
                                            )
                                        }) : <li class="nav-item mt-2"><Skeleton count={3} /></li>}
                                              </div>
                                              </div>
                                   
                                </section>

                            </div>

                        </div>

                        <div class="col-lg-4 mt-auto">

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Tutor</small>
                                <small><strong>{lname ? lname : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Cost</small>
                                <small><strong>{details.course_cost ? `₦${details.course_cost}` : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Category</small>
                                <small><strong>{details.category ? details.category : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Who can take this Course?</small>
                                <small><strong>Expert</strong></small>
                            </div>

                            {details.course_title ? <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                {access ? <Link to={`/preview-course/${code}`} class="btn d-flex mx-auto mb-2" type="submit" disabled={!details.course_title}><b><small>Preview Course</small></b></Link> :
                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={!details.course_title} onClick={addToCart}><b><small><i class="fad fa-shopping-cart"></i> Add to Cart</small></b></button>}
                            </div> : ''}
                        </div>
                    </div>

                </div>
            </section>


            <RelatedCourses />
            <Footer />
        </>
    )
}

export default CourseDetails
