import axios from 'axios'
import React,{useEffect, useState} from 'react'
import Skeleton from 'react-loading-skeleton'
import Moment from 'react-moment'
import { Baseurl } from '../baseurl'

const CourseReview = ({code}) => {
    const [review, setReview] = useState([])
    useEffect(() => {
        axios.get(`${Baseurl}coursereview`, {
            params:{code:code}
        }).then((res) => {
            if (res.data) {
                setReview(res.data) 
                console.log(res.data) 
            }
        })
    }, [])
    return (
        <>
    {/* <div class="container mb-5">
    <div class="row d-flex">
        <div class="col-md-8">
            <div class="d-flex justify-content-between align-items-center">
                <small style={{color:'#0D826E'}}><strong>Reviews ({review.length})</strong></small>
            </div>
                {review.length ? review.map((item, index)=>{
                    return(
                        <>
               <div class="d-flex justify-content-between align-items-center">
                    <div class="d-flex flex-row align-items-center"> 
                    <span><small style={{color:'#071B4D'}}><strong>{item.lastname}</strong></small> : <small class="font-weight-bold">{item.review}</small></span> </div> <small></small>
                </div>
                        </>
                    )
                }):<Skeleton count={3}/>}
            </div>
        </div>
    </div> */}
    <div class="container d-flex justify-content-center mb-100">
    <div class="row">
        <div class="col-md-12">
            <div class="rvcard">
                <div class="rvcard-body">
                    <p class="rvcard-title"><strong>Recent Reviews</strong></p>
                    <small class="rvcard-subtitle">Latest Reviews by students</small>
                </div>
               {review.length ? review.map((item, index)=>{
                   return(
                       <>
              <div class="comment-widgets m-b-20" key={ index}>
                    <div class="d-flex flex-row comment-row">
                        <div class="p-2"><span class="round"><img src="https://i.imgur.com/uIgDDDd.jpg" alt="user" width="50"/></span></div>
                        <div class="comment-text w-100">
                            <p><strong>{item.lastname}</strong></p>
                            <p class="comment-footer"> <span class="date">April 14, 2019</span>  <span class="action-icons">  <a href="" data-abc="true"><i class="fa fa-heart"></i></a> </span> </p>
                            <small class="m-b-5 m-t-10">{item.review}</small>
                        </div>
                    </div>
                </div>
                       </>
                   )
               }):<Skeleton count={4}/>}
            </div>
        </div>
    </div>
</div>
        </>
    )
}

export default CourseReview
