import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import ScriptTag from 'react-script-tag';
import { useLocation } from 'react-router';
const Navbar = (props) => {
const [showDashboard, setShowDashboard] = useState(false);
 useEffect(() => {
      let token = JSON.parse(sessionStorage.Token)
      if(token === true){
      setShowDashboard(true)
      }else{
          setShowDashboard(showDashboard)
      }
    }, [])
    return (
        <>
            <ScriptTag isHydrating={false} type="text/javascript" src="main.js" />
            {/* <!-- ======= Header ======= --> */}
            <header id="header" class="fixed-top">
                <div class="container d-flex align-items-center">

                    <h1 class="logo me-auto"><Link to='/'>Fromhome<span style={{ color: '#05A081', fontSize: '25px' }}>.</span></Link></h1>
                    <nav id="navbar" class="navbar order-last order-lg-0 ">
                        <ul>
                            {props.home ? <li><Link to='/'>Home</Link></li>: ''}
                            <li><Link to='/login'>About</Link></li>
                            <li><Link to='/available-courses'>Courses</Link></li>
                            {showDashboard ? <li><Link to='/dashboard'>Dashboard</Link></li> : ''}
                            {showDashboard ? <li><Link to='/Dashboard'>Tutors' Portal</Link></li>:''}
                            <li><Link to='/login'>Login</Link></li>
                            <li><Link to='/register'>Register</Link></li>
                            <li><Link to='/login'>Contact</Link></li>
                        </ul>
                        <i class="bi bi-list mobile-nav-toggle"></i>
                    </nav>
                    {/* <!-- .navbar --> */}

                    <a href="courses.html" class="get-started-btn">Be a Tutor <i class="fal fa-arrow-right"></i></a>
                    <Link to='/cart' className='ml-2 chart'><strong><i class="fad fa-shopping-cart"></i></strong></Link>
                </div>
            </header>
            {/* <!-- End Header --> */}


        </>
    )
}

export default Navbar
