import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { Link } from 'react-router-dom'


const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [course, setCourse] = useState({})
    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setCourse(res.data)
            }
        })

    }, [])




    const addToCart = () => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            let courseDetails = {
                title: course.course_title, code: course.course_code,
                cost: course.course_cost, category: course.category, tutor: 'Great'
            }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        } else {
            let cart = [];
            let courseDetails = { courseCode: code }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        }
    }


    const addwishlist = () => {
        let token = sessionStorage.Token
        axios.post(`${Baseurl}addwishlist`, { code: code }, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            if(res.data === 'added'){
                alert('added')
            }else if(res.data === 'alreadyAdded'){
                alert('already')
            }
        }).catch((error) => {
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })
    }
    return (
        <>
            <Navbar />
        
        <section id="course-details" class="course-details mt-4">
         <div class="container" data-aos="fade-up">
         <div class="section-title">
       
         <p>aaa</p>
         </div>
        
         <div class="row">
          <div class="col-lg-8">
            aaaaaa
         <h6><small>ss</small><small>ss</small></h6>
         
           <p>
              aaa
           </p>
          </div>
          <div class="col-lg-4">

            <div class="course-info d-flex justify-content-between align-items-center">
              <h5>Trainer</h5>
              <p><a>sss</a></p>
            </div>

            <div class="course-info d-flex justify-content-between align-items-center">
              <h5>Course Fee</h5>
                 <p>aaa</p>
            </div>

            <div class="course-info d-flex justify-content-between align-items-center">
              <h5>Number of Students</h5>
                 <p>sss</p>
            </div>

         <div class="course-info d-flex justify-content-between align-items-center">
              <h5>Date Created</h5>
              <p>ss</p>
        </div>
        
        
          </div>
        </div>
        
      </div>
       </section>
            {/* <button style={{ marginTop: '100px' }} onClick={addToCart}>Add to cart</button>
            <button style={{ marginTop: '100px' }} onClick={addwishlist}>wishlist</button> */}
            <Footer />
        </>
    )
}

export default CourseDetails
