import React, {useState} from 'react'
import BackTotop from './back_to_top'
import Footer from './footer'
import Navbar from './navbar'
import SearchResult from './search_result'
import TrendingCourses from './trending_courses'
import { ReactSearchAutocomplete } from 'react-search-autocomplete'
import axios from 'axios'
import { Baseurl } from '../baseurl'


const AvailableCourses = () => {
    document.title = 'Fromhome | Ava'
    const [search, setSearch] = useState('')
    const [searchResult, setSearchResult] = useState([])
    const [isSubmitting, setSubmitting] = useState(false)
    const items = [
        {
          id: 0,
          name: 'Cobol'
        },
        {
          id: 1,
          name: 'Javascript'
        },
        {
          id: 2,
          name: 'Basic'
        },
        {
          id: 3,
          name: 'PHP'
        },
        {
          id: 4,
          name: 'Java'
        },
        {
            id: 5,
            name: 'Boostrap'
          },
          {
            id: 6,
            name: 'Marketing'
          }
      ]

      const handleOnSelect = (item) => {
        setSearch(item)
      }
    

      const formatResult = (item) => {
       return item;
       
      }


      const searchCourse = () =>{
        setSubmitting(true)
         axios.get(`${Baseurl}searchcourse`, {
             params:search
         }).then((res)=>{
           setSubmitting(false)
          setSearchResult(res.data)
          
      
         })
      }
    return (
        <>
          <Navbar/>
            <section className="about container-fluid mt-4" id="about">
                <div className='container-fluid'>
                    <div className='row'>
                        <div className='col-md-8 col-sm mx-auto'>
                            <img src="assets/img/logo.png" alt="" /> <br />
                            <small><strong><i class="fad fa-search" style={{ color: '#071B4D' }}></i> Search for a course</strong></small>
                            <small>
                            <ReactSearchAutocomplete
                             items={items}
                             onSelect={handleOnSelect}
                             autoFocus
                             formatResult={formatResult}
                             placeholder='Search by coursename or category'
                              />
                            </small>
                            <button class="btn d-flex mx-auto" type="submit" onClick={searchCourse}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Search</b></button>
                        </div>

                    </div>
                </div>
                <div className="mt-4">
                    <SearchResult  result={searchResult} submit={isSubmitting}/>
                </div>


                <div className="mt-4">
                    <TrendingCourses />
                </div>
            </section>
            
            <BackTotop/>
            <Footer/>
        </>
    )
}

export default AvailableCourses
