import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Baseurl } from '../../baseurl';

const Sections = () => {
    useEffect(() => {
      let course = JSON.parse(localStorage.c) 
    }, [])
    const [showloader, setShowloader] = useState(false)
    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-md-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />


                            <p>📚<strong>Stage 2:</strong> Course Sections</p>
                            <small>*Add all course sections</small>
                            <Formik
                                initialValues={{section: ''}}
                                validate={values => {
                                    const errors = {};
                                    if (!values.section) {
                                        errors.section = '*At least a section is required';
                                    }
                                   



                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    setTimeout(() => {
                                        // setShowloader(true)
                                        // if (sessionStorage.Token) {
                                        //     let token = sessionStorage.Token
                                        //     values.token = token
                                        //     axios.post(`${Baseurl}beatutor`, values).then((res) => {
                                        //         if(res.data === 'alreadyActivated'){
                                        //             setShowloader(false)
                                        //         toast("😔 Tutor portal already created!", {
                                        //             autoClose: 2000,
                                        //         })
                                        //         setTimeout(() => history.push('/tutor-portal'), 2000)
                                        //         }else if(res.data === 'tutorActivated'){
                                        //             setShowloader(false)
                                        //             toast("👍 Tutor portal  created!", {
                                        //                 autoClose: 2000,
                                        //             })
                                        //         }
                                        //     }).catch((error)=>{
                                        //       if(error.response.status){
                                        //           setShowloader(false)
                                        //         toast("😔 You need to be logged in!", {
                                        //             autoClose: 2000,
                                        //         })
                                        //         setTimeout(() => history.push('/login'), 2000)
                                        //         sessionStorage.setItem('Path', path.pathname)

                                        //       }
                                        //     })
                                        // } else {
                                        //     setShowloader(false)
                                        //     toast("😔 You need to be logged in!", {
                                        //         autoClose: 2000,
                                        //     })
                                        //     setTimeout(() => history.push('/login'), 2000)
                                        //     sessionStorage.setItem('Path', path.pathname)
                                        // }
                                        // setSubmitting(false);
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                         <div class="row-1">
                                            <div className='row'>
                                                <div className='col-md-6 mx-auto py-2 text-center'>
                                                    <small><strong><i class="fad fa-trash-alt text-danger"></i> Section 1</strong></small>
                                                  <div className='mb-2 py-1 text-light' style={{backgroundColor:'#071B4D', borderRadius:'5px'}}>
                                                    <small><strong>Passing of props</strong></small>
                                                  </div>
                                                </div>
                                                <div className='col-md-6 mx-auto py-2 text-center'>
                                                <small><strong><i class="fad fa-check-square"></i> Section 2</strong></small>
                                                <div className='mb-2 py-1 text-light' style={{backgroundColor:'#071B4D', borderRadius:'5px'}}>
                                                  <small>Addition, division, multiplicationssss</small>
                                                  </div>
                                                </div>
                                                <div className='col-md-6 mx-auto py-2 text-center'>
                                                <small><strong><i class="fad fa-check-square"></i> Section 3</strong></small>
                                                  <div className='py-1 text-light' style={{backgroundColor:'#071B4D', borderRadius:'5px'}}>
                                                    <small>sssss</small>
                                                  </div>
                                                </div>

                                            </div>
                                            
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Section Names</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Type in section names" name="section" />
                                                <ErrorMessage name="section" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <small style={{color:'#0D826E', cursor:'pointer'}}><strong><i class="fad fa-plus-square" style={{color:'#071B4D'}}></i> Click to Add</strong></small>

                                        {/* <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Course Cost</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="Course Cost" name="experience" />
                                                <ErrorMessage name="experience" component="small" className='text-danger' />
                                            </div>
                                        </div> */}

                                        {/* <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Description</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="Course Description" name="experience" />
                                                <ErrorMessage name="experience" component="small" className='text-danger' />
                                            </div>
                                        </div> */}

                                        <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Save Sections and Proceed</b></button>
                                        <small onClick={()=>window.location.replace('basic-details')} style={{cursor:'pointer'}}><strong><i class="fal fa-long-arrow-left" style={{color:'#0D826E'}}></i> Edit Previous Stage</strong></small>
                                    </Form>
                                )}
                            </Formik>

                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default Sections
