import React from 'react'
import { Link } from 'react-router-dom'
import Footer from '../landing_page/footer'
import Navbar from '../landing_page/navbar'
import { BrowserRouter as Route, Switch } from 'react-router-dom';
import Home from './home';
import MyCourses from './my_courses';
import Wishlists from './wishlists';
import Transactions from './transactions';
import { useLocation } from 'react-router';

const Dashboard = () => {
    let path = useLocation()
    return (
        
        <>
        <Navbar dashboard={path.pathname}/>
          {/* dashboard section starts */}
 <section id="dashboard" className="d-flex justify-content-center align-items-center">
                <div className="container position-relative" data-aos="zoom-in" data-aos-delay="100">
                    <h1>Great<span style={{ color: '#05A081', fontSize: '25px' }}>.</span></h1>
                    <h2>Welcome, the world is now yours!</h2>
                    <Link to="/dashboard/my-courses"><a className="btn-get-started">My Courses <i class="fal fa-arrow-right"></i></a></Link>
                </div>
            </section>

            <Switch>
                <Route exact path='/dashboard'>
                    <Home/>
                </Route>

                <Route  path='/dashboard/my-courses'>
                    <MyCourses/>
                </Route>

                <Route  path='/dashboard/wishlists'>
                    <Wishlists/>
                </Route>

                <Route  path='/dashboard/transactions'>
                    <Transactions/>
                </Route>

            </Switch>
            {/* dashboard section Ends */} 
            <Footer/>  
        </>
    )
}

export default Dashboard
