import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [course, setCourse] = useState({})
    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setCourse(res.data)
            }
        })

    }, [])




    const addToCart = () => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            let courseDetails = {
                title: course.course_title, code: course.course_code,
                cost: course.course_cost, category: course.category, tutor: 'Great'
            }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
            toast("ðŸ˜” Added to Cart!", {
                autoClose: 2000,
              })
        } else {
            let cart = [];
            let courseDetails = { courseCode: code }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        }
    }


    const addwishlist = () => {
        let token = sessionStorage.Token
        axios.post(`${Baseurl}addwishlist`, { code: code }, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            if(res.data === 'added'){
                toast("ðŸ˜” Added!", {
                    autoClose: 2000,
                  })
            }else if(res.data === 'alreadyAdded'){
                toast("ðŸ˜” Already Added!", {
                    autoClose: 2000,
                  })
            }
        }).catch((error) => {
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })
    }
    return (
        <>
        <Navbar/>
        <small><ToastContainer /></small>
        <section id="course-details" class="course-details mt-4">
         <div class="container" data-aos="fade-up">
         <p><strong><i class="fad fa-info-square" style={{color:'#0D826E'}}></i> {course.course_title}</strong><br/>
         <span class="badge" style={{bac}}>{course.category}</span></p>
         
        
         <div class="row">
          <div class="col-lg-7 mx-auto">
          </div>
          <div class="col-lg-5 mx-auto">
              <p style={{color:'#071B4D'}}><strong>Course Details</strong></p>
            <div class="course-info d-flex justify-content-between align-items-center rounded">
              <h5>Trainer</h5>
              <p>great</p>
            </div>

            <div class="course-info d-flex justify-content-between align-items-center rounded">
              <h5>Course Cost</h5>
                 <p>{course.course_cost}</p>
            </div>

            <div class="course-info d-flex justify-content-between align-items-center rounded">
              <h5>Number of Students</h5>
                 <p>sss</p>
            </div>

         <div class="course-info d-flex justify-content-between align-items-center rounded">
              <h5>Date Created</h5>
              <p>ss</p>
        </div>
        
        <button class="btn d-flex mx-auto" type="submit" onClick={addToCart}><small><i class="fad fa-shopping-cart"></i> Add to Cart</small></button>
        <button class="btn d-flex mx-auto" type="submit" onClick={addwishlist}><small><i class="fad fa-heart-circle"></i> Add to Wishlist</small></button>
        
          </div>
        </div>
        
      </div>
       </section>
            <Footer />
        </>
    )
}

export default CourseDetails
