import React from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useLocation } from 'react-router'
import { Formik, Form, Field, ErrorMessage } from 'formik';

const BeTutor = () => {
    let path = useLocation();
    return (
        <>
         <Navbar BeTutor={path.pathname}/>
         <section className="about container-fluid mt-3" id="about">
                <div className="row">
                    <div className="col-md-8 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />

                            <small><strong>Create a Tutor's Portal</strong></small>

                            <Formik
                                initialValues={{ firstName: '', lastName: '', experience: '', password: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.firstName) {
                                        errors.firstName = '*FirstName Required';
                                    }
                                    else if (!values.lastName) {
                                        errors.lastName = '*LastName Required';
                                    }
                                    else if(!values.experience){
                                        errors.experience = '*Experience Required';
                                    }
                                     else if (!values.password) {
                                        errors.password = '*Password Required';
                                    }
                                    

                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    setTimeout(() => {
                                        alert(JSON.stringify(values, null, 2));
                                        setSubmitting(false);
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>


                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">FirstName</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="FirstName" name="firstName" />
                                                <ErrorMessage name="firstName" component="small" className='text-danger' /> </div>
                                        </div>


                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">LastName</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="LastName" name="lastName" />
                                                <ErrorMessage name="lastName" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Experience</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="How long have you been a Tutor?" name="experience" />
                                                <ErrorMessage name="experience" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                                            <div class="row row-2"> <Field type="password" placeholder="Password" name="password" />
                                                <ErrorMessage name="password" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>Create</b></button>
                                    </Form>
                                )}
                            </Formik>
                            
                        </div>
                    </div>

                </div>
            </section>
         <Footer/>   
        </>
    )
}

export default BeTutor
