import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Baseurl } from '../../baseurl';

const Sections = () => {
    const [formikPersist, setFormikPersist] = useState({})
    const [sectionArray, setSectionArray] = useState([])
    useEffect(() => {
      let getCourse = JSON.parse(localStorage.getItem('course')) 
      setFormikPersist(getCourse)
      let values = formikPersist.values
      let section = values.sections
      sectionArray(section)
    //   console.log(section)
    //   if(getCourse){
    //     setCourse(getCourse.values)
      
    //   }else{
    //     setCourse({}) 
    //   }

    //   let sectionArray = localStorage.getItem('course');
    //   if(sectionArray){
    //     setSection(JSON.parse(sectionArray).values.sections) 
        
    //   }else{
    //       setSection([])
    //   }
      
      
    }, [])

    // useEffect(() => {
    //    if(JSON.parse(localStorage.getItem('courseform'))){
    //     setShowsection(JSON.parse(localStorage.getItem('courseform')).sections)
    //    }else{
    //     setShowsection([])
    //    }
    // }, [])
    const [showloader, setShowloader] = useState(false)
    const [course, setCourse] = useState({})
    const [section, setSection] = useState([])
    // const [showSection, setShowsection] = useState([])
    

    
    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-md-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />


                            <p>📚<strong>Stage 2:</strong> Course Sections</p>
                            <small>*Add all course sections</small>
                            <Formik
                                initialValues={{section: ''}}
                                validate={values => {
                                    const errors = {};
                                    if (!values.section) {
                                        errors.section = '*At least a section is required';
                                    }
                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    setTimeout(() => {
                                        sectionArray.push(values)
                                        // section.push(values)
                                        // if(section){
                                        //   section.push(values)
                                        //   localStorage.setItem('course', JSON.stringify(course))
                                        //   let sectionArray = localStorage.getItem('course'); 
                                        //   setSection(JSON.parse(sectionArray).section) 
                                        // }
                                       
                
                                        
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                          <div class="row-1">
                                            <div className='row'>

                                                {section.map((item, index)=>{
                                                    return(
                                                        
                                                <div className='col-md-6 mx-auto py-2 text-center'>
                                                    <small><strong><i class="fad fa-trash-alt text-danger"></i> Section {index+1}</strong></small>
                                                  <div className='mb-2 py-1 text-light' style={{backgroundColor:'#071B4D', borderRadius:'5px'}}>
                                                    <small className='text-capitalize'><strong>{item.section}</strong></small>
                                                  </div>
                                                </div>
                                                       
                                                    )
                                                })}
                                              

                                            </div>
                                            
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Section Names</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Type in section names" name="section" />
                                                <ErrorMessage name="section" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button className='border-0 bg-transparent outline-0' style={{color:'#0D826E', cursor:'pointer'}} type="submit" ><i class="fad fa-plus-square" style={{color:'#071B4D'}}></i><small><strong> Click to Add</strong></small></button>


                                          </Form>
                                )}
                            </Formik>
                            <button class="btn d-flex mx-auto mb-2" type="submit"><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Save Sections and Proceed</b></button>
                            <small onClick={()=>window.location.replace('basic-details')} style={{cursor:'pointer'}}><strong><i class="fal fa-long-arrow-left" style={{color:'#0D826E'}}></i> Edit Previous Stage</strong></small>
                                 

                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default Sections
