import axios from 'axios';
import React, {useEffect, useState} from 'react'
import { Link } from 'react-router-dom';
import { Baseurl } from '../../baseurl';



const MyCourses = () => {
    const [details, setDetails] = useState([])
    useEffect(() => {
        let token = sessionStorage.Token
        axios.get(`${Baseurl}purchasedcourse`, {
           headers:{
            'Authorization': 'Bearer' + token
           }
        }).then((res)=>{
           if(res.data){
           setDetails(res.data)
           }
        })
    }, [])
    
    return (
        <>
            <section className="about container-fluid mt-4 mx-auto" id="about">
                <div class="row">
                    {details.length ? '' : <div className='col-md-6 col-sm mx-auto text-center mt-4'>
                        <h5>Your have no Course</h5>
                        <i class="fas fa-books text-danger fa-3x"></i>
                        <Link to='/available-courses'><button class="btn d-flex mx-auto" type="submit" ><b>Buy a Course</b></button></Link>
                    </div>}

                    <small><strong>Purhcased Courses</strong></small>
                   
                   {details.length ? details.map((item, index)=>{
                       return(
                           <>

                           </>
                       )
                   }):''}
                   
                  
                   
                </div>

            </section>
        </>
    )
}

export default MyCourses
