import axios from 'axios'
import React, { useState, useEffect } from 'react'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'
import { Baseurl } from '../../baseurl'
import { useLocation } from 'react-router'



const Transactions = () => {
    let path = useLocation()
    const [details, setDetails] = useState([])
    useEffect(() => {
        let token = sessionStorage.Token
        axios.get(`${Baseurl}transactions`, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            if (res.data) {
                setDetails(res.data)
            }
        }).catch((error) => {
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })

    }, [])
    return (
        <>
            <section className="about container-fluid mt-4" id="about">
                <div className='row'>
                    <div className='col-md-6 col-sm mx-auto text-center'>
                        <h5>You have no transaction history</h5>
                        <i class="fad fa-shopping-cart fa-3x text-danger"></i>
                        <Link to='/available-courses'><button class="btn d-flex mx-auto" type="submit" ><b>Buy a Course</b></button></Link>
                    </div>
                    <div className='col-md-8 col-sm mx-auto'>
                        <div class="container  px-2">
                            <small style={{ color: '#0D826E' }}><strong>Transactions History</strong></small>
                            <div class="table-responsive mt-2">
                                <table class="table table-responsive table-borderless">
                                    <thead>
                                        <tr class="bg-light">
                                            <th scope="col" width="5%">#</th>
                                            <th scope="col" width="20%"><small>Category</small></th>
                                            <th scope="col" width="10%"><small>Course</small></th>
                                            <th scope="col" width="20%"><small>Amount</small></th>
                                            <th scope="col" width="20%"><small>Status</small></th>


                                        </tr>
                                    </thead>
                                    {details.length ? details.map((item, index) => {
                                        return (
                                            <>
                                                <tbody key={index}>
                                                    <tr>
                                                        <td><small>{index + 1}</small></td>
                                                        <td><small>{item.category}</small></td>
                                                        <td><small>{item.course_title}</small></td>
                                                        <td><small>{item.amount}</small></td>
                                                        <td className={item.state === 'pending' ? ' text-danger text-capitalize': 'text-capitalize'}><small><strong>{item.state}</strong></small></td>

                                                    </tr>
                                                </tbody>
                                            </>
                                        )
                                    }) : <tbody>
                                        <tr>
                                            <td><small><Skeleton count={1} /></small></td>
                                            <td><small><Skeleton count={1} /></small></td>
                                            <td><small><Skeleton count={1} /></small></td>
                                            <td><small><Skeleton count={1} /></small></td>

                                        </tr>
                                    </tbody>}
                                </table>

                            </div>
                        </div>
                    </div>

                </div>

            </section>
        </>
    )
}

export default Transactions
