import React, {useEffect, useState} from 'react'
import { Link as Scroll } from 'react-scroll'
const BackTotop = () => {
  const [active, setActive] = useState(true)
  

  useEffect(() => {
    if (window.scrollY > 100) { 
     setActive()
    }
  }, [])
    return (
       
        <>
            <Scroll to='hero'><a href="" class={active ? 'back-to-top d-flex align-items-center justify-content-center active' : 'back-to-top d-flex align-items-center justify-content-center'}><i class="bi bi-arrow-up-short"></i></a>
            </Scroll> 
        </>
    )
}

export default BackTotop
