import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom'
import RelatedCourses from './related_courses'
import Skeleton from 'react-loading-skeleton'



const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [details, setDetails] = useState({})
    const [sections, setSections] = useState([])
    const [lname, setLname] = useState('')
    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setDetails(res.data.details)
                setSections(res.data.sections)
                setLname(res.data.lname)
            }
        })

    }, [])

    const addToCart = () => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            let courseDetails = {
                title: details.course_title, code: details.course_code,
                cost: details.course_cost, category: details.category, tutor: 'Great'
            }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
            toast("👍 Added to Cart!", {
                autoClose: 2000,
            })
        } else {
            let cart = [];
            let courseDetails = { courseCode: code }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        }
    }

    const addwishlist = () => {
        let token = sessionStorage.Token
        if (token) {
            axios.post(`${Baseurl}addwishlist`, { code: code }, {
                headers: {
                    'Authorization': 'Bearer' + token
                }
            }).then((res) => {
                if (res.data === 'added') {
                    toast("👍 Added to wishlists!", {
                        autoClose: 2000,
                    })
                } else if (res.data === 'alreadyAdded') {
                    toast("😔 Already Added!", {
                        autoClose: 2000,
                    })
                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        } else {
            toast("😔 Login to Wishlist!", {
                autoClose: 2000,
            })
            
        }
    }

    return (
        <>
            <Navbar />
            <section id="course-details" class="course-details">
                <small><ToastContainer /></small>
                <div class="container" data-aos="fade-in">
                    <div class="row">
                        <div class="col-lg-8">
                            <img src="assets/img/course-details.jpg" class="img-fluid" alt="" />
                            <h3>{details.course_title ? details.course_title : <h3><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></h3>}</h3>
                            <p>
                                {details.description ? details.description : <p><Skeleton count={4} /></p>}
                            </p>
                            <div className='mt-3'>
                                {details.course_title ? <small onClick={addToCart} style={{ color: '#071B4D', cursor: 'pointer' }} className='px-2'><strong><i class="fad fa-shopping-cart"></i> Add to Cart</strong></small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}
                                {details.course_title ? <small onClick={addwishlist} style={{ color: '#071B4D', cursor: 'pointer' }} className='px-2'><i class="fas fa-bookmark"></i><strong> Wishlist</strong></small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}
                                {details.course_title ? <small style={{ color: '#071B4D' }} className='px-2'><i class="fad fa-user-alt"></i><strong> 1200 Enrolled</strong></small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}
                                {details.course_title ? <small style={{ color: '#071B4D' }} className='px-2'><strong>
                                    <i class="fas fa-star" style={{ color: '#FFD700', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i> </strong>
                                </small> : <small className='px-2'><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}


                               <div className='login-card mt-4' style={{borderRadius:'8px'}}>
                                    <div class="p-5 learn">
                                        <h6 class="fw-bold  text-left p-3 mx-auto">
                                            Course Sections
                                        </h6>

                                        <div class="row">
                                            {sections.map((item, index) => {
                                                return (
                                                    <>
                                                        <div class="col-md-6 p-2">
                                                            <small><i class="fa fa-check-circle me-3"></i>{item.section_name}</small>
                                                        </div>
                                                    </>
                                                )
                                            })}
                                        </div>
                                    </div>
                                </div>

                            </div>

                        </div>

                        <div class="col-lg-4 mt-5">

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Tutor</small>
                                <small><strong>{lname ? lname : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Cost</small>
                                <small><strong>{details.course_cost ? `₦${details.course_cost}` : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Category</small>
                                <small><strong>{details.category ? details.category : <small><i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i></small>}</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <small>Who can take this Course?</small>
                                <small><strong>Expert</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{ borderRadius: '8px' }}>
                                <button class="btn d-flex mx-auto mb-2" type="submit" disabled={!details.course_title}><b><small>Buy Course</small></b></button>
                            </div>

                        </div>
                    </div>

                </div>
            </section>


            <RelatedCourses />
            <Footer />
        </>
    )
}

export default CourseDetails
