import axios from 'axios'
import React, { useState, useEffect } from 'react'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'
import { Baseurl } from '../../baseurl'
import { useLocation } from 'react-router'



const Transactions = () => {
    let path = useLocation()
    const [details, setDetails] = useState([])
    const [isFetching, setFecthing] = useState(false)

    useEffect(() => {
        setFecthing(true)
        let token = sessionStorage.Token
        axios.get(`${Baseurl}transactions`, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            setFecthing(false)
            if (res.data) {
                setDetails(res.data)
            }
        }).catch((error) => {
            setFecthing(false)
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })

    }, [])
    return (
        <>
            <section className="about container-fluid mt-4" id="about">
                <div className='row'>
                  
               
                    
                    <div className='col-md-8 col-sm mx-auto pt-2'>
                        <div class="container  px-2">
                            <small style={{ color: '#0D826E' }}><strong>Transactions History</strong></small>
                            <div class="table-responsive mt-2">
                            
                                    <table class="table table-responsive table-borderless">
                                    <thead>
                                        <tr class="bg-light">
                                            <th scope="col" width="5%">#</th>
                                            <th scope="col" width="20%"><small>Category</small></th>
                                            <th scope="col" width="10%"><small>Course</small></th>
                                            <th scope="col" width="20%"><small>Amount</small></th>
                                            <th scope="col" width="20%"><small>Status</small></th>

                                        </tr>
                                    </thead>
                                   
                                </table>}
                                

                            </div>
                        </div>
                    </div>

                </div>

            </section>
        </>
    )
}

export default Transactions

