import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import Navbar from './navbar';
import Footer from './footer'
import { Baseurl } from '../baseurl';
import axios from 'axios';
import Latest_courses from './latest_courses';
import Back_to_top from './back_to_top';
import { Formik, Form, Field, ErrorMessage } from 'formik';


const Payment = () => {
  return (
    <>

      <Navbar />
      <section className="about container-fluid mt-4" id="about">
        <div className="row">
          <div className="col-md-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
            <div class="login-card" style={{ borderRadius: '10px' }}>
            <img class="img-fluid" src="https://img.icons8.com/color/48/000000/mastercard-logo.png" /><img class="img-fluid" src="https://img.icons8.com/color/48/000000/visa.png" /> <br />
              <small><strong>Payment Page</strong></small>

              <Formik
                initialValues={{ email: '', password: '' }}
                validate={values => {
                  const errors = {};
                  if (!values.email) {
                    errors.email = '*Email Required';
                  } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                  ) {
                    errors.email = 'Invalid email address';
                  } else if (!values.password) {
                    errors.password = '*Password Required';
                  }

                  return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    setSubmitting(false);
                  }, 400);
                }}
              >
                {({ isSubmitting }) => (

                  <Form>
                     <div class="row row-1">
   
            <div class="col-7"> <input type="text" placeholder="**** **** **** 3193"/> </div>
            <div class="col-3 d-flex justify-content-center"> <a href="#">Remove card</a> </div>
        </div>
                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                      <div class="row row-2"> <Field type="email" placeholder="Registered Email" name="email" />
                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                    </div>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                      <div class="row row-2"> <Field type="password" placeholder="Password" name="password" />
                        <ErrorMessage name="password" component="small" className='text-danger' />
                      </div>
                    </div>
                    <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>Proceed</b></button>
                  </Form>
                )}
              </Formik>
              <div className='mt-3 text-center'>
              <small style={{color:'#071B4D'}}><strong>Powered by:</strong> <img class="img-fluid" src="/assets/img/paystack.png"  style={{width:'18%'}}/></small>

              </div>
              </div>
          </div>

        </div>
      </section>
<Footer/>
    </>
  );
};

export default Payment;
