import axios from 'axios'
import React,{useEffect, useState} from 'react'
import Skeleton from 'react-loading-skeleton'
import Moment from 'react-moment'
import { Baseurl } from '../baseurl'

const CourseReview = ({code}) => {
    const [review, setReview] = useState([])
    const [isFetching, setFecthing] = useState(false)
    useEffect(() => {
        setFecthing(true)
        axios.get(`${Baseurl}coursereview`, {
            params:{code:code}
        }).then((res) => {
            setFecthing(false)
            if (res.data) {
                setReview(res.data) 
                console.log(res.data) 
            }
        })
    }, [])
    return (
        <>
    <div class="container mb-5">
    <div class="row">
        <div class="col-md">
            <div class="rvcard">
            <p class="rvcard-title"><strong>Recent Reviews</strong></p>
                {review.length ? <div class="rvcard-body">
                    <small class="rvcard-subtitle">Latest Reviews by students</small>
                </div>:''}
             {isFetching ? <Skeleton count={3}/> :
             <>
               {review.length ? review.map((item, index)=>{
                   return(
                       <>
              <div class="comment-widgets" key={index}>
                    <div class="d-flex flex-row comment-row">
                        <div class="p-2"><span class="round"><img src="https://i.imgur.com/uIgDDDd.jpg" alt="user" width="50"/></span></div>
                        <div class="comment-text">
                            <p><strong>{item.lastname}</strong></p>
                            <small class="comment-footer"> <span class="date"><Moment fromNow>{item.created_at}</Moment></span>  <span class="action-icons">  <a href="" data-abc="true"><i class="fa fa-heart"></i></a> </span> </small><br/>
                            <small>{item.review}</small>
                        </div>
                    </div>
                </div>
                       </>
                   )
               }):
               <div class="comment-text">
               <small><strong>No Review</strong></small>
           </div>}</>}
            </div>
        </div>
    </div>
</div>
        </>
    )
}

export default CourseReview
