import axios from 'axios'
import React, { useState, useEffect } from 'react'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'
import { Baseurl } from '../../baseurl'
import { useLocation } from 'react-router'



const Transactions = () => {
    let path = useLocation()
    const [details, setDetails] = useState([])
    const [isFetching, setFecthing] = useState(false)

    useEffect(() => {
        setFecthing(true)
        let token = sessionStorage.Token
        axios.get(`${Baseurl}transactions`, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            setFecthing(false)
            if (res.data) {
                setDetails(res.data)
            }
        }).catch((error) => {
            setFecthing(false)
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })

    }, [])
    return (
        <>
            <section className="about container-fluid mt-4" id="about">
                <div className='row'>

                    {isFetching ? <div className='col-md-8 col-sm mx-auto pt-2'>
                        <Skeleton count={4} />
                    </div> : <></>}

                </div>

            </section>
        </>
    )
}

export default Transactions




