import React, {useEffect, useState} from 'react'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios';
import { Baseurl } from '../baseurl';
import Skeleton from 'react-loading-skeleton';



const Preview = () => {
  const { code } = useParams();
  const [sections, setSections] = useState([])
  const [files, setFiles] = useState([])
  useEffect(() => {
    axios.get(`${Baseurl}coursepreview`, {
      params: { code: code }
  }).then((res) => {
      if (res.data) {
         setSections(res.data)
        // setFiles(res.data)
        console.log(res.data)
        res.data.forEach(element => {
          setFiles(element['no_of_files'])
        });
      }
  })
  }, [])


  // const getFiles = (index) =>{
  //   setSections(sections)
  //   axios.get(`${Baseurl}coursefiles`, {
  //     params: {id: index}
  // }).then((res) => {
  //     if (res.data) {
  //       setFiles(res.data)
  //     }
  // })
  // }
  return (
    <>
      <Navbar />
      <section class="container-fluid">
        <small><strong>📺 Playing, Best regards!</strong></small>
        <div class="row mx-auto">
          <div class="col-md-8 align-items-center vid-cont">
            <video width="100%" controls class="video mt-2 border" autoplay loop playsinline style={{ borderRadius: '10px' }}>
              <source src="/assets/img/lesson1.mp4" type="video/mp4" />
            </video>
          </div>

          <div class="col-md-4 bg-dark mt-2 px-0 accord-section" style={{ overflowY: 'scroll' }}>
            <small><strong>📚 Course Preview</strong></small>
            <ul class="nav nav-pills p-2 mb-3 justify-content-center" id="pills-tab" role="tablist">
              <li class="nav-item" role="presentation">
                <button class="nav-link active" id="pills-home-tab" data-bs-toggle="pill" data-bs-target="#pills-home" type="button" role="tab" aria-controls="pills-home" aria-selected="true"><small><strong>Sections</strong></small></button>
              </li>
              <li class="nav-item" role="presentation">
                <button class="nav-link" id="pills-profile-tab" data-bs-toggle="pill" data-bs-target="#pills-profile" type="button" role="tab" aria-controls="pills-profile" aria-selected="false"><small><strong>Note</strong></small></button>
              </li>
              <li class="nav-item" role="presentation">
                <button class="nav-link" id="pills-contact-tab" data-bs-toggle="pill" data-bs-target="#pills-contact" type="button" role="tab" aria-controls="pills-contact" aria-selected="false"><small><strong>More</strong></small></button>
              </li>
            </ul>


            <div class="tab-content" id="pills-tabContent">
              <div class="tab-pane fade show active" id="pills-home" role="tabpanel" aria-labelledby="pills-home-tab">

                {sections.length ? sections.map((item, index)=>{
                  return(
                    <>
                <div class="accordion" id="accordionExample">
                  <div class="accordion-item">
                    <h2 class="accordion-header" id="headingOne">
                      <button class="accordion-button fw-bold text-white border-0 rounded-0" type="button" data-bs-toggle="collapse" data-bs-target={`#${item.section_name}`} aria-expanded="true" aria-controls={item.section_name} >
                        <small className='text-capitalize'>{item.section_name}</small>
                      </button>
                    </h2>
                    <div id={item.section_name} class="accordion-collapse collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                      <div class="accordion-body bg-secondary py-0">
                        <div class="row align-items-center course-part py-2">
                          {/* {sections.no_of_files.map((file, index)=>{
                            return(
                              <>
                            <div class="col-2"><i class="fa fa-angle-double-right"></i></div>
                          <div class="col-7 text-wrap" key={index}><small>{file.file_name}</small></div>
                          <div class="col-3"><small><strong>2m</strong></small></div>
                          </>
                              
                            )
                          })} */}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                    </>
                  )
                }):<div class="accordion mt-2"><Skeleton count={3}/></div>}
              
              
              </div>


              <div class="tab-pane fade" id="pills-profile" role="tabpanel" aria-labelledby="pills-profile-tab">
                <h5 class="p-3">Write down your note here</h5>
              </div>
              <div class="tab-pane fade" id="pills-contact" role="tabpanel" aria-labelledby="pills-contact-tab">
                <h5 class="p-3">More options are coming soon</h5>
              </div>
            </div>

          </div>

        </div>
      </section>

    </>
  )
}

export default Preview
