import React from 'react'
import Footer from '../landing_page/footer'
import Navbar from '../landing_page/navbar'
import { useLocation } from 'react-router'
import { Formik, Form, Field, ErrorMessage } from 'formik';

const CreateTutorPortal = () => {
    let path = useLocation();
    return (
        <>
            <Navbar BeTutor={path.pathname} />
            <section className="about container-fluid mt-3" id="about">
                <div className="row">
                    <div className="col-md-8 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />

                            <small><strong>üë®‚Äçüè´ Create a Tutor's Portal</strong></small>

                            <Formik
                                initialValues={{ firstName: '', lastName: '', experience: '', password: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.firstName) {
                                        errors.firstName = '*FirstName Required';
                                    }
                                    else if (!values.lastName) {
                                        errors.lastName = '*LastName Required';
                                    }
                                    else if (!values.experience) {
                                        errors.experience = '*Experience Required';
                                    }
                                    else if (!values.password) {
                                        errors.password = '*Password Required';
                                    }


                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    setTimeout(() => {
                                        alert(JSON.stringify(values, null, 2));
                                        setSubmitting(false);
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Experience</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="How long have you been a Tutor?" name="experience" />
                                                <ErrorMessage name="experience" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Category</span> </div>
                                            <div class="row row-2">
                                                {/* <Field type="password" placeholder="Password" name="password" /> */}
                                                <select class="form-select form-select-sm mt-1" aria-label=".form-select-sm example" name='category'>
                                                    <option selected>Choose a category</option>
                                                    <option value="1">Design</option>
                                                    <option value="2">Marketing</option>
                                                    <option value="3">IT and Software</option>
                                                    <option value="3">Photograpy</option>
                                                    <option value="3">Engineering</option>
                                                    <option value="3">Pure Sciences</option>
                                                    <option value="3">Agriculture</option>
                                                    <option value="3">Business</option>

                                                </select>
                                                <ErrorMessage name="password" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Purpose</span> </div>
                                            <div class="row row-2">
                                                {/* <Field type="password" placeholder="Password" name="password" /> */}
                                                <select class="form-select form-select-sm mt-1" aria-label=".form-select-sm example" name='category'>
                                                    <option selected>Choose a purpose</option>
                                                    <option value="1">For-Profit</option>
                                                    <option value="2">Not-for-Profit</option>
                            

                                                </select>
                                                <ErrorMessage name="password" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>Create</b></button>
                                    </Form>
                                )}
                            </Formik>

                        </div>
                    </div>

                </div>
            </section>
            <Footer />
        </>
    )
}

export default CreateTutorPortal
