import React from 'react'
import BackTotop from './back_to_top'
import Footer from './footer'
import LatestCourses from './latest_courses'
import Navbar from './navbar'
import SearchResult from './search_result'
import TrendingCourses from './trending_courses'
import { ReactSearchAutocomplete } from 'react-search-autocomplete'


const AvailableCourses = () => {
    const searchCourses = () => {
        // document.getElementById("myDropdown").classList.toggle("show")
    }

    const items = [
        {
          id: 0,
          name: 'Cobol'
        },
        {
          id: 1,
          name: 'JavaScript'
        },
        {
          id: 2,
          name: 'Basic'
        },
        {
          id: 3,
          name: 'PHP'
        },
        {
          id: 4,
          name: 'Java'
        }
      ]

      const handleOnSearch = (string, results) => {
        // onSearch will have as the first callback parameter
        // the string searched and for the second the results.
        console.log(string, results)
      }
    
      const handleOnHover = (result) => {
        // the item hovered
        console.log(result)
      }
    
      const handleOnSelect = (item) => {
        // the item selected
        console.log(item)
      }
    
      const handleOnFocus = () => {
        console.log('Focused')
      }
    
      const formatResult = (item) => {
        return item;
       // return (<p dangerouslySetInnerHTML={{__html: '<strong>'+item+'</strong>'}}></p>); //To format result as html
      }
    return (
        <>
          <Navbar/>
            <section className="about container-fluid mt-4" id="about">
                <div className='container-fluid'>
                    <div className='row'>

                        <div className='col-md-8 col-sm mx-auto'>
                            <img src="assets/img/logo.png" alt="" /> <br />
                            <small><strong><i class="fad fa-search" style={{ color: '#071B4D' }}></i> Search for a course</strong></small>
                            <form>
                            <ReactSearchAutocomplete
                             items={items}
                             onSearch={handleOnSearch}
                             onHover={handleOnHover}
                             onSelect={handleOnSelect}
                             onFocus={handleOnFocus}
                             autoFocus
            formatResult={formatResult}
          />
                                <button class="btn d-flex mx-auto" type="submit" ><b>Search</b></button>

                            </form>
                        </div>

                    </div>
                </div>
                <div className="mt-5">
                    <SearchResult />
                </div>

                <div className="mt-5">
                    <LatestCourses />
                </div>

                <div className="mt-5">
                    <TrendingCourses />
                </div>
            </section>
            
            <BackTotop/>
            <Footer/>
        </>
    )
}

export default AvailableCourses
