import axios from 'axios'
import React,{useEffect, useState} from 'react'
import Skeleton from 'react-loading-skeleton'
import Moment from 'react-moment'
import { Baseurl } from '../baseurl'

const CourseReview = ({code}) => {
    const [review, setReview] = useState([])
    useEffect(() => {
        axios.get(`${Baseurl}coursereview`, {
            params:{code:code}
        }).then((res) => {
            if (res.data) {
                setReview(res.data) 
                console.log(res.data) 
            }
        })
    }, [])
    return (
        <>
    <div class="container mb-5">
    <div class="row d-flex">
        <div class="col-md-8">
            <div class="d-flex justify-content-between align-items-center">
                <small style={{color:'#0D826E'}}><strong>Reviews ({review.length})</strong></small>
            </div>
                {review.length ? review.map((item, index)=>{
                    return(
                        <>
               <div class="d-flex justify-content-between align-items-center">
                    <div class="d-flex flex-row align-items-center"> 
                    <span><small style={{color:'#071B4D'}}><strong>{item.lastname}</strong></small> :<br/> <small class="font-weight-bold">{item.review}</small></span> </div> <small></small>
                </div>
                        </>
                    )
                }):<Skeleton count={3}/>}
            </div>
        </div>
    </div>
        </>
    )
}

export default CourseReview
