import React, {useEffect, useState} from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'

const CourseDetails = () => {
    const {code} = useParams();
    const [course, setCourse] = useState({})
    useEffect(() => {
      axios.get(`${Baseurl}coursedetails`, {
          params:{code:code}
      }).then((res)=>{
       if(res.data){
        setCourse(res.data)
       }
      })

    }, [])




    const addToCart = () =>{
     let cart = JSON.parse(localStorage.getItem('cart'))
     if(cart){
     let courseDetails = {title:course.course_title, code:course.course_code, qty:1,
     cost:course.course_cost, newCost:course.course_cost, categ}
     cart.push(courseDetails);
     localStorage.setItem('cart', JSON.stringify(cart))
     }else{
     let cart = [];
     let courseDetails = {courseCode:code}
     cart.push(courseDetails);
     localStorage.setItem('cart', JSON.stringify(cart))
     }
    }
    return (
    <>
     <Navbar/>
     <button style={{marginTop:'100px'}} onClick={addToCart}>Add to cart</button>
     <Footer/>
        </>
    )
}

export default CourseDetails
