import React, { useEffect, useState } from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom'
import RelatedCourses from './related_courses'



const CourseDetails = () => {
    let path = useLocation()
    const { code } = useParams();
    const [course, setCourse] = useState({})
    useEffect(() => {
        axios.get(`${Baseurl}coursedetails`, {
            params: { code: code }
        }).then((res) => {
            if (res.data) {
                setCourse(res.data)
            }
        })

    }, [])

    const addToCart = () => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            let courseDetails = {
                title: course.course_title, code: course.course_code,
                cost: course.course_cost, category: course.category, tutor: 'Great'
            }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
            toast("ðŸ‘ Added to Cart!", {
                autoClose: 2000,
            })
        } else {
            let cart = [];
            let courseDetails = { courseCode: code }
            cart.push(courseDetails);
            localStorage.setItem('cart', JSON.stringify(cart))
        }
    }

    const addwishlist = () => {
        let token = sessionStorage.Token
        axios.post(`${Baseurl}addwishlist`, { code: code }, {
            headers: {
                'Authorization': 'Bearer' + token
            }
        }).then((res) => {
            if(res.data === 'added'){
                toast("ðŸ‘ Added to wishlists!", {
                    autoClose: 2000,
                  })
            }else if(res.data === 'alreadyAdded'){
                toast("ðŸ˜” Already Added!", {
                    autoClose: 2000,
                  })
            }
        }).catch((error) => {
            if (error.response.status === 500) {
                sessionStorage.setItem('Path', path.pathname)
            }
        })
    }

    return (
        <>
            <Navbar />

            <section id="course-details" class="course-details">
                <small><ToastContainer /></small>
                <div class="container" data-aos="fade-in">
                    <div class="row">
                        <div class="col-lg-8">
                            <img src="assets/img/course-details.jpg" class="img-fluid" alt="" />
                            <h3>{course.course_title}</h3>
                            <p>
                                {course.description}
                            </p>
                            <div className='mt-3'>
                                <small onClick={addToCart} style={{ color: '#0D826E', cursor: 'pointer' }} className='px-2'><strong><i class="fad fa-shopping-cart"></i> Add to Cart</strong></small>
                                <small onClick={addwishlist} style={{ color: '#0D826E', cursor: 'pointer' }} className='px-2'><i class="fas fa-bookmark"></i><strong> Wishlist</strong></small>
                                <small style={{ color: '#0D826E' }} className='px-2'><i class="fad fa-user-alt"></i><strong> 1200 Enrolled</strong></small>
                                <small style={{ color: '#0D826E' }} className='px-2'><strong>
                                    <i class="fas fa-star" style={{ color: '#FFD700', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i>
                                    <i class="far fa-star" style={{ color: '#071B4D', fontSize: '13px' }}></i> </strong></small>
                            </div>
                        </div>
                        <div class="col-lg-4 mt-5">

                            <div class="course-info d-flex justify-content-between align-items-center" style={{borderRadius:'8px'}}>
                                <small>Tutor</small>
                                <small><strong>Walter White</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{borderRadius:'8px'}}>
                                <small>Course Fee</small>
                                <small><strong>$165</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{borderRadius:'8px'}}>
                                <small>Available Seats</small>
                                <small><strong>30</strong></small>
                            </div>

                            <div class="course-info d-flex justify-content-between align-items-center" style={{borderRadius:'8px'}}>
                                <small>Schedule</small>
                                <small><strong>5.00 pm - 7.00 pm</strong></small>
                            </div>

                        </div>
                    </div>

                </div>
            </section>
            <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Login</b></button>

            <RelatedCourses />
            <Footer />
        </>
    )
}

export default CourseDetails
