import React, { useState } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import Navbar from './navbar';
import Footer from './footer';
import axios from 'axios';
import { Baseurl } from '../baseurl';
import { useHistory } from 'react-router';



const PasswordReset = () => {
    const history = useHistory();
    const [showPasss, setShowPass] = useState(false)
    return (
        <>
            <Navbar />
            <section className="about container-fluid mt-4" id="about">
                <div className='container-fluid'>
                    <div className="row">
                        <div className="col-md-6 col-sm mx-auto">
                            <div class="login-card" style={{ borderRadius: '10px' }}>
                                <img src="assets/img/logo.png" alt="" /> <br />
                                <small><strong>Reset Your Password</strong></small>
                                {/* Check email */}
                                {showPasss === false ? <Formik
                                    initialValues={{ email: ''}}
                                    validate={values => {
                                        const errors = {};
                                        if (!values.email) {
                                            errors.email = '*Email Required';
                                        } else if (
                                            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                                        ) {
                                            errors.email = 'Invalid email address';
                                        }

                                        return errors;
                                    }}
                                    onSubmit={(values, { setSubmitting }) => {
                                        setTimeout(() => {
                                            axios.post(`${Baseurl}checkemail`, values).then((res) => {
                                                if (res.data === 'codesent') {
                                                    setSubmitting(false);
                                                    setShowPass(true);
                                                }
                                            })

                                        }, 400);
                                    }} >
                                    {({ isSubmitting }) => (
                                        <Form>
                                            <div class="row-1">
                                                <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                                                <div class="row row-2"> <Field type="email" placeholder="Registered Email" name="email" />
                                                    <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                                            </div>
                                            <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Proceed</b></button>
                                        </Form>
                                    )}
                                </Formik> : ''}
                                
                                {/* Verification Code */}
                                {showPasss ? <Formik
                                    initialValues={{code: ''}}
                                    validate={values => {
                                        const errors = {};
                                        if (!values.code) {
                                            errors.code = '*verification Code Required';
                                        } 

                                        return errors;
                                    }}
                                    onSubmit={(values, { setSubmitting }) => {
                                        setTimeout(() => {
                                            axios.post(`${Baseurl}verifycode`, values).then((res) => {
                                                // if (res.data === 'codesent') {
                                                //     setSubmitting(false);
                                                //     setShowPass(true);
                                                // }
                                                console.log(res.data)
                                            })

                                        }, 400);
                                    }} >
                                    {({ isSubmitting }) => (
                                        <Form>
                                            <div class="row-1">
                                                <div class="row row-2"> <span id="login-card-inner">4-digit Code</span> </div>
                                                <div class="row row-2"> <Field type="number" placeholder="Enter verification code" name="code" />
                                                    <ErrorMessage name="code" component="small" className='text-danger' />
                                                </div>
                                            </div>
                                            <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Proceed</b></button>
                                        </Form>
                                    )}
                                </Formik> : ''}
                            </div>
                        </div>

                    </div>

                </div>
            </section>
            <Footer />
        </>
    )
}

export default PasswordReset
