import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Baseurl } from '../../baseurl';
import { Persist } from 'formik-persist';
import { useHistory } from 'react-router';

const Sections = () => {
    const history = useHistory()
    const [formikPersist, setFormikPersist] = useState({})
    const [sectionArray, setSectionArray] = useState([])

    useEffect(() => {
        let getCourse = JSON.parse(localStorage.getItem('course'))
        if (getCourse) {
            setFormikPersist(getCourse)

        } else {
            setFormikPersist({})

        }

        let section = getCourse.values.sections
        if (section) {
            setSectionArray(section)
        } else {
            setSectionArray([])
        }

    }, [])


    const deleteSection = (i) => {
        const newVar = [...sectionArray]
        newVar.splice(i, 1);
        setSectionArray(newVar)
        let newForm = formikPersist
        newForm.values.sections = newVar
        localStorage.setItem('course', JSON.stringify(newForm))

    }

    const saveSection = () =>{
    let values = formikPersist.values
    if(values.category && values.cost && values.desc && values.title){
     alert('sss')
    }
    
    else{
        alert('xxx')
    }

    }
    const [showloader, setShowloader] = useState(false)

    return (
        <>
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-lg-9 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />


                            <p>ðŸ“š<strong>Stage 2:</strong> Course Sections</p>
                            <small><strong>*Add all course sections</strong></small>
                            <Formik
                                initialValues={{ section: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.section) {
                                        errors.section = '*Section name is required';
                                    }
                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    if (formikPersist.values.category && formikPersist.values.cost && formikPersist.values.desc && formikPersist.values.title) {
                                        sectionArray.push(values)
                                        setSectionArray(sectionArray)
                                        localStorage.setItem('course', JSON.stringify(formikPersist))

                                    } else {
                                        toast("ðŸ‘ Please fill in basic details!", {
                                            autoClose: 2000,
                                        })
                                        setTimeout(() => history.goBack(), 2000)
                                    }


                                    setSubmitting(false);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        {sectionArray.length ? <div class="row-1">
                                            <div className='row'>

                                                {sectionArray.map((item, index) => {
                                                    return (

                                                        <div className='col-md-6 mx-auto py-2 text-center'>
                                                            <small><strong><i class="fad fa-trash-alt text-danger" onClick={() => deleteSection(index)}></i> Section {index + 1}</strong></small>
                                                            <div className='mb-2 py-1 text-light section-loop'>
                                                                <small className='text-capitalize'>{item.section}</small>
                                                            </div>
                                                        </div>

                                                    )
                                                })}


                                            </div>

                                        </div> : ''}

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Section Names</span> </div>
                                            <div class="row row-2"> <Field type="text" placeholder="Type in section names" name="section" />
                                                <ErrorMessage name="section" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button className='border-0 bg-transparent outline-0' style={{ color: '#0D826E', cursor: 'pointer' }} type="submit" ><i class="fad fa-plus-square fa-2x" style={{ color: '#071B4D' }}></i><small><strong> Click to Add</strong></small></button>


                                    </Form>
                                )}
                            </Formik>
                            <button class="btn d-flex mx-auto mb-2" type="submit" onClick={saveSection}><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Save Sections and Proceed</b></button>
                            <small onClick={() => window.location.replace('basic-details')} style={{ cursor: 'pointer' }}><strong><i class="fal fa-long-arrow-left" style={{ color: '#0D826E' }}></i> Edit Previous Stage</strong></small>


                        </div>
                    </div>

                </div>
            </section>
        </>
    )
}

export default Sections
