import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useLocation } from 'react-router';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Withdraw = ({profile}) => {
    return (
        <>
           <div class="page-content page-container mx-auto mb-5" id="page-content" style={{ marginTop: '100px' }}>
                <div class="row container d-flex justify-content-center mx-auto">
                    <div class="col-md">
                        <div class="profile-card user-card-full">
                            <div class="row m-l-0 m-r-0">
                                <div class="col-sm-4 bg-c-lite-green user-profile">
                                    <div class="card-block text-center text-white">
                                        <div class="m-b-25"> <img src="https://img.icons8.com/bubbles/100/000000/user.png" class="img-radius" alt="User" /> </div>
                                        <h6 class="f-w-600">{profile.firstname ? profile.firstname : <i class="fas fa-spinner fa-spin" style={{ color: '#ffff' }}></i>} {profile.lastname}</h6>
                                        <h6><strong>{`Balance: â‚¦${profile.earnings ? profile.earnings : 0}`}</strong></h6>
                                    </div>
                                </div>
                                <div class="col-sm-8">
                                    <div class="card-block">
                                        <h6 class="m-b-20 p-b-5 b-b-default f-w-600">Earnings Withdrawal</h6>
                                        <Formik
                initialValues={{ email: '', password: '' }}
                validate={values => {
                  const errors = {};
                  if (!values.email) {
                    errors.email = '*Email Required';
                  } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                  ) {
                    errors.email = 'Invalid email address';
                  } else if (!values.password) {
                    errors.password = '*Password Required';
                  }

                  return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                    // dispatch(isLogged(true));
                    // history.push('/dashboard')

                    axios.post(`${Baseurl}auth/login`, values).then((res) => {
                      if (res.data) {
                        setSubmitting(false);
                        let token = res.data.access_token
                        sessionStorage.setItem('Token', token)
                        dispatch(isLogged(true));
                        if (sessionStorage.Path) {
                          history.push(sessionStorage.Path)
                          sessionStorage.removeItem('Path')
                        } else {
                          history.push('/dashboard')
                        }
                      }
                    }).catch((error) => {
                      if (error.response.status === 401) {
                        setSubmitting(false);
                        toast("ðŸ˜” Login details incorrect!", {
                          autoClose: 3000,
                        })
                      } else if (error.response.status === 500) {
                        setSubmitting(false);
                        sessionStorage.removeItem('Token')
                        toast("ðŸ˜” Server is down!", {
                          autoClose: 3000,
                        })
                      }
                    })

                  }, 400);
                }}
              >
                {({ isSubmitting }) => (

                  <Form>
                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                      <div class="row row-2"> <Field type="email" placeholder="Registered Email" name="email" />
                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                    </div>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                      <div class="row row-2"> <Field type="password" placeholder="Password" name="password" />
                        <ErrorMessage name="password" component="small" className='text-danger' />
                      </div>
                    </div>
                    <button class="btn d-flex mx-auto mb-2" type="submit" disabled={isSubmitting}><b>{isSubmitting ? <i class="fas fa-spinner fa-spin"></i> : ''} Login</b></button>
                  </Form>
                )}
              </Formik>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>   
        </>
    )
}

export default Withdraw
