import axios from 'axios'
import React, {useEffect, useState} from 'react'
import { Baseurl } from '../baseurl'
import { useLocation } from 'react-router'
import Skeleton from 'react-loading-skeleton'
import { Link } from 'react-router-dom'

const Latest_courses = () => {
    const [course, setCourse] = useState([])
    const path = useLocation();

    useEffect(() => {
            axios.get(`${Baseurl}latestcourses`, {  
            }).then((res) => {
                if (res.data) {
                    setCourse(res.data)
                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
       

    }, [])
    return (
        <>
            <div class="container mb-5">
                <div class="row">
                    <small><strong>Latest Courses</strong></small>
                    
                    
                   {course.length ? course.map((item, index)=>{
                       return(
                           <>
                           <div class="col-md-4 mt-2" data-aos="fade-in" data-aos-delay="100">
                           <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                               <div class="d-flex justify-content-between">
                                   <div class="d-flex flex-row align-items-center">
                                       <div class="icon">  <i class="fab fa-html5 fa-2x" style={{ color: '#ffbb2c' }}></i> </div>
                                       <div class="ms-2 c-details">
                                           <h6 class="mb-0">Tutor</h6> <span>1 days ago</span>
                                       </div>
                                   </div>
                                   <div class="badge"> <span style={{ color: '#0054D2', backgroundColor: '#CFEEFA' }}>Latest</span> </div>
                               </div>
                               <div class="mt-2">
                                   <h6 class="heading">{item.course_title}</h6>
                                   <div class="mt-2">
                                       <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i>
                                       <div class="mt-3"> <Link class="text2"><strong> Preview</strong></Link> </div>  </div>
                               </div>
                           </div>
                       </div>
                           </>
                       )
                   }) :<div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>}
                 
                  
                </div>
            </div>
        </>
    )
}

export default Latest_courses
