import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Baseurl } from '../../baseurl';
import { useHistory, useLocation } from 'react-router';
import Skeleton from 'react-loading-skeleton';
import Moment from 'react-moment';

const CreatedCourses = () => {
    const history = useHistory();
    const [course, setCourse] = useState([])
    const path = useLocation();
    const [isFetching, setFecthing] = useState(false)

    useEffect(() => {
        setFecthing(true)
        let token = sessionStorage.Token
        if (token) {
            axios.get(`${Baseurl}createdcourse`, {
                params: {
                    token: token
                }
            }).then((res) => {
                setFecthing(false)
                if (res.data) {
                    setCourse(res.data)
                }
            }).catch((error) => {
                setFecthing(false)
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        } else {

        }

    }, [])
    return (
        <>
            <div className='container mb-3'>
                <div className='row' style={{ marginTop: '90px' }}>
                    <div className='col-md-6 col-sm mx-auto text-center'>
                        <h5>Your have {course.length ? course.length : 'no'} course{course.length > 1 ? 's' : ''}</h5>
                        <i class="fas fa-books text-danger fa-3x"></i>
                       <button class="btn d-flex mx-auto" onClick={()=>history.push('/dashboard/create-a-course')} ><b><small>Create a new course</small></b></button>
                    </div>

                </div>

            </div>
            <div className='container mb-4'>
                <div class="row">
                    {course.length ? <small style={{color:'#0D826E'}}><strong>Created Courses</strong></small> : <small ><strong>No Course</strong></small>}
                    {isFetching ? <div className='row'>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                    <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                </div> : <>{course.length ? course.map((item, index) => {
                        return (
                            <>
                                <div class="col-md-4 mt-2" data-aos="fade-in" data-aos-delay="100">
                                    <div class="card p-4 mb-2 shadow" style={{ borderRadius: '20px' }} >
                                        <div class="d-flex justify-content-between">
                                            <div class="d-flex flex-row align-items-center">
                                                <div class="icon">  <i class="fab fa-html5 fa-2x" style={{ color: '#ffbb2c' }}></i> </div>
                                                <div class="ms-2 c-details">
                                                     <span><Moment fromNow>{item.created_at}</Moment></span>
                                                </div>
                                            </div>
                                            <div class="badge text-capitalize"> <span style={{ color: '#FFFFFF', backgroundColor: '#F75B00'}}>{item.status}</span> </div>
                                        </div>
                                        <div class="mt-2">
                                            <Link to={`/dashboard/created-courses/manage/${item.course_code}`} class="text2"><strong>{item.course_title}</strong></Link>
                                            
                                        </div>
                                    </div>
                                </div>
                            </>
                        )
                    }) : ''}</>}
 
                </div>
            </div>






        </>
    )
}

export default CreatedCourses
