import React, {useState} from 'react'
import Footer from '../landing_page/footer'
import Navbar from '../landing_page/navbar'
import { useHistory, useLocation } from 'react-router'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import axios from 'axios';
import { Baseurl } from '../baseurl';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const CreateTutorPortal = () => {
    const [showloader, setShowloader] = useState(false)
    const path = useLocation();
    const history = useHistory();
    return (
        <>
            <Navbar BeTutor={path.pathname} />
            <section className="about container-fluid mt-3" id="about">
                <small><ToastContainer /></small>
                <div className="row">
                    <div className="col-md-8 col-sm mx-auto" data-aos="fade-in" data-aos-delay="100">
                        <div class="login-card" style={{ borderRadius: '10px' }}>
                            <img src="assets/img/logo.png" alt="" /> <br />

                            <small><strong>👨‍🏫 Create a Tutor's Portal</strong></small>

                            <Formik
                                initialValues={{ experience: '', category: '', purpose: '', token: '' }}
                                validate={values => {
                                    const errors = {};
                                    if (!values.experience) {
                                        errors.experience = '*Years of experience Required';
                                    }
                                    else if (!values.category) {
                                        errors.category = '*Category Required';
                                    }
                                    else if (!values.purpose) {
                                        errors.purpose = '*Purpose Required';
                                    }



                                    return errors;
                                }}
                                onSubmit={(values, { setSubmitting }) => {
                                    setTimeout(() => {
                                        setShowloader(true)
                                        if (sessionStorage.Token) {
                                            let token = sessionStorage.Token
                                            values.token = token
                                            axios.post(`${Baseurl}beatutor`, values).then((res) => {
                                                if(res.data === 'alreadyActivated'){
                                                    setShowloader(false)
                                                toast("😔 Tutor portal already created!", {
                                                    autoClose: 2000,
                                                })
                                                setTimeout(() => history.push('/login'), 2000)
                                                }else if(res.data === 'tutorActivated'){
                                                    setShowloader(false)
                                                    toast("👍 Tutor portal  created!", {
                                                        autoClose: 2000,
                                                    })
                                                }
                                            }).catch((error)=>{
                                              if(error.response.status){
                                                  setShowloader(false)
                                                toast("😔 You need to be logged in!", {
                                                    autoClose: 2000,
                                                })
                                                setTimeout(() => history.push('/login'), 2000)
                                                sessionStorage.setItem('Path', path.pathname)
                                            
                                              }
                                            })
                                        } else {
                                            setShowloader(false)
                                            toast("😔 You need to be logged in!", {
                                                autoClose: 2000,
                                            })
                                            setTimeout(() => history.push('/login'), 2000)
                                            sessionStorage.setItem('Path', path.pathname)
                                        }
                                        setSubmitting(false);
                                    }, 400);
                                }}
                            >
                                {({ isSubmitting }) => (

                                    <Form>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Experience</span> </div>
                                            <div class="row row-2"> <Field type="number" placeholder="How long have you been a Tutor?" name="experience" />
                                                <ErrorMessage name="experience" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Category</span> </div>
                                            <div class="row row-2">
                                                <Field as="select" name="category" class="form-select form-select-sm mt-1" aria-label=".form-select-sm example">
                                                    <option selected>Choose a category</option>
                                                    <option value="Design">Design</option>
                                                    <option value="Marketing">Marketing</option>
                                                    <option value="IT and Software">IT and Software</option>
                                                    <option value="Photograpy">Photograpy</option>
                                                    <option value="Engineering">Engineering</option>
                                                    <option value="Engineering">Engineering</option>
                                                    <option value="Agriculture">Agriculture</option>
                                                    <option value="Business">Business</option>
                                                </Field>
                                                <ErrorMessage name="category" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <div class="row-1">
                                            <div class="row row-2"> <span id="login-card-inner">Purpose</span> </div>
                                            <div class="row row-2">
                                                <Field as="select" name="purpose" class="form-select form-select-sm mt-1" aria-label=".form-select-sm example">
                                                    <option selected>Choose a purpose</option>
                                                    <option value="For-Profit">For-Profit</option>
                                                    <option value="Not-for-Profit">Not-for-Profit</option>
                                                </Field>
                                                <ErrorMessage name="purpose" component="small" className='text-danger' />
                                            </div>
                                        </div>

                                        <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>{showloader ? <i class="fas fa-spinner fa-spin"></i> : ''} Create</b></button>
                                    </Form>
                                )}
                            </Formik>

                        </div>
                    </div>

                </div>
            </section>
            <Footer />
        </>
    )
}

export default CreateTutorPortal
