const Login = () => {

  return (
    <>
      <Navbar />
      <section className="about container-fluid mt-4" id="about">
        <div className="row">
          <div className="col-lg-5  d-lg-block order-2 order-lg-1" data-aos="fade-in" data-aos-delay="100">
            <small className='px-3 pb-2'><strong><i class="fad fa-chalkboard-teacher" style={{ color: '#0D826E' }}></i> Learn a skill today, Be smart!</strong></small>

            <img src="assets/img/student2.jpg" className="img-fluid login-img mt-2" alt="" style={{ borderRadius: '10px' }} />
          </div>

          <div className="col-lg-6 order-1 order-lg-2 mb-4" data-aos="fade-in" data-aos-delay="100">


            <div class="login-card" style={{ borderRadius: '10px' }}>
              <div class="section-title">
                <h2><strong>LOGIN PAGE</strong></h2>
              </div>

              <form>


                <div class="row-1">
                  <div class="row row-2"> <span id="login-card-inner">Email</span> </div>
                  <div class="row row-2"> <input type="text" placeholder="Email" /> </div>
                </div>


                <div class="row-1">
                  <div class="row row-2"> <span id="login-card-inner">Password</span> </div>
                  <div class="row row-2"> <input type="text" placeholder="Password" /> </div>
                </div>
                <button class="btn d-flex mx-auto"><b>Login</b></button>
              </form>
            </div>
          </div>

        </div>
      </section>

      <Latest_courses />
      <Back_to_top />
      <Footer />
    </>
  );
};

export default Login;
