import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import ScriptTag from 'react-script-tag';
import { useLocation } from 'react-router';
import {Link as Scroll} from 'react-scroll'




const Navbar = ({home, login, register, dashboard}) => {
const [showDashboard, setShowDashboard] = useState(false);
 useEffect(() => {
      let token = JSON.parse(sessionStorage.Token)
      if(token === true){
      setShowDashboard(true)
      }else{
          setShowDashboard(showDashboard)
      }
    }, [])
    return (
        <>
            <ScriptTag isHydrating={false} type="text/javascript" src="main.js" />
            {/* <!-- ======= Header ======= --> */}
            <header id="header" class="fixed-top">
                <div class="container d-flex align-items-center">

                    <h1 class="logo me-auto"><Link to='/'>Fromhome<span style={{ color: '#05A081', fontSize: '25px' }}>.</span></Link></h1>
                    <nav id="navbar" class="navbar order-last order-lg-0 ">
                        <ul>
                            {showDashboard  ? <li><Link to='/dashboard'>Dashboard</Link></li> : ''}
                            {showDashboard ? <li><Link to='/Dashboard'>Tutors' Portal</Link></li>:''}
                            {home ? <li><Scroll to='about' style={{cursor: 'pointer'}}>About</Scroll></li>:''}
                            <li><Link to='/available-courses'>Courses</Link></li>
                            {dashboard ? <li><Link to='/dashboard/transactions'>Transactions</Link></li>:''}
                            {dashboard ? <li><Link to='/dashboard/wishlists'>Wishlists</Link></li>:''}
                            {dashboard ? <li><Link to='/dashboard/myprofile'>Profile</Link></li>:''}
                             {home ||  register ? <li><Link to='/login'>Login</Link></li>: ''}
                            {home || login  ? <li><Link to='/register'>Register</Link></li>: ''}
                            <li><Scroll to='footer' style={{cursor: 'pointer'}}>Contact</Scroll></li>
                        </ul>
                        <i class="bi bi-list mobile-nav-toggle"></i>
                    </nav>
                    {/* <!-- .navbar --> */}

                    <Link to='/be-a-tutor' href="courses.html" class="get-started-btn">Be a Tutor <i class="fal fa-arrow-right"></i></Link>
                    <Link to='/cart' className='ml-2 chart'><strong><i class="fad fa-shopping-cart"></i></strong></Link>
                </div>
            </header>
            {/* <!-- End Header --> */}


        </>
    )
}

export default Navbar
