import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import BackTotop from './back_to_top'
import Footer from './footer'
import Navbar from './navbar'
import TrendingCourses from './trending_courses'


const Cart = () => {
    const [cart, setCart] = useState([])
    useEffect(() => {
        let cart = JSON.parse(localStorage.getItem('cart'))
        if (cart) {
            setCart(cart)
        }
    }, [])

    const deleteItem = (index) => {
        const newCart = [...cart]
        newCart.splice(index, 1);
        setCart(newCart)
        let localStorageCart = JSON.parse(localStorage.getItem('cart'))
        localStorageCart = newCart
        localStorage.setItem('cart', JSON.stringify(localStorageCart))
    }


    const decrease = (index) => {
        const newCart = [...cart]
        for (let i = 0; i < newCart.length; i++) {
            if (index === i) {
                if (newCart[i].qty > 1) {
                    newCart[i].qty--
                    let newQty = newCart[i].qty
                    newCart[i].newCost = newCart[i].cost * newQty
                    setCart(newCart)
                    let localStorageCart = JSON.parse(localStorage.getItem('cart'))
                    localStorageCart = newCart
                    localStorage.setItem('cart', JSON.stringify(localStorageCart))
                }
            }
        }
    }

    const increase = (index) => {
        const newCart = [...cart]
        for (let i = 0; i < newCart.length; i++) {
            if (index === i) {
                newCart[i].qty++;
                let newQty = newCart[i].qty
                newCart[i].newCost = newCart[i].cost * newQty
                setCart(newCart)
                let localStorageCart = JSON.parse(localStorage.getItem('cart'))
                localStorageCart = newCart
                localStorage.setItem('cart', JSON.stringify(localStorageCart))
            }
        }


    }

    const preparePayment = () =>{
        
    }


    return (
        <>
            <Navbar />
            <section className="about container-fluid mt-3" id="about">
                {cart.length ? '' : <div className='row'>
                    <div className='col-md-6 col-sm mx-auto text-center mt-4'>
                        <h5>Your Cart is empty</h5>
                        <i class="fad fa-shopping-cart fa-3x text-danger"></i>
                        <Link to='/available-courses' class="btn d-flex mx-auto" type="submit" ><b>Keep shopping</b></Link>
                    </div>
                </div>}
                <div class="container mt-2">
                    <div class="d-flex justify-content-center row">
                        <div class="col-md-8 col-sm mx-auto">
                            {cart.length ? <small><strong><i class="fad fa-shopping-cart" style={{ color: '#071B4D' }}></i> Your Shopping Cart</strong></small> : ''}
                            {cart.map((item, index) => {
                                return (
                                    <>
                                        <div class="d-flex flex-row justify-content-between align-items-center p-2  mt-4 px-3 rounded" style={{ backgroundColor: '#F6F7F6' }}>
                                            <div class="mr-1">  <i class="fab fa-react fa-3x" style={{ color: " #e361ff" }}></i></div>
                                            <div class="d-flex flex-column align-items-center product-details"><span class="font-weight-bold">Basic T-shirt</span>
                                                <div class="d-flex flex-row product-desc">
                                                    <div class="size mr-1"><span class="text-grey">Size:</span><span class="font-weight-bold">&nbsp;M</span></div>
                                                    <div class="color"><span class="text-grey">Color:</span><span class="font-weight-bold">&nbsp;Grey</span></div>
                                                </div>
                                            </div>
                                            <div class="d-flex flex-row align-items-center qty"><i class="fa fa-minus text-danger" onClick={() => decrease(index)}></i>
                                                <h5 class="text-grey mt-1 mr-1 ml-1"> {item.qty} </h5><i class="fa fa-plus text-success" onClick={() => increase(index)}></i>
                                            </div>
                                            <div>
                                                <h5 class="text-grey">${item.newCost}</h5>
                                            </div>
                                            <div class="d-flex align-items-center" onClick={() => deleteItem(index)}><i class="fa fa-trash mb-1 text-danger"></i></div>
                                        </div>
                                        
                                    </>
                                )
                            })}

                            {cart.length ? <button to='payment' class="btn btn-warning btn-block btn-lg ml-2 pay-button" type="button" onClick={preparePayment}><small>Proceed to pay <strong>{cart.reduce(function (total, obj) { return total + obj.newCost}, 0)}</strong></small></button> : ''}
                        </div>
                    </div>
                </div>
            </section>

            <TrendingCourses />
            <BackTotop />
            <Footer />
        </>
    )
}

export default Cart
