import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Baseurl } from '../../baseurl';
import { useLocation } from 'react-router';
import Skeleton from 'react-loading-skeleton';
import Moment from 'react-moment';

const CreatedCourses = () => {
    const [course, setCourse] = useState([])
    const path = useLocation();

    useEffect(() => {
        let token = sessionStorage.Token
        if (token) {
            axios.get(`${Baseurl}createdcourse`, {
                params: {
                    token: token
                }
            }).then((res) => {
                if (res.data) {

                    setCourse(res.data)

                }
            }).catch((error) => {
                if (error.response.status === 500) {
                    sessionStorage.setItem('Path', path.pathname)
                }
            })
        } else {

        }

    }, [])
    return (
        <>
        <div className='container mb-3'>
            <div className='row' style={{marginTop:'90px'}}>
            <div className='col-md-6 col-sm mx-auto text-center'>
                        <h5>Your have {course.length >= 1 ? course.length : course.length  ? <i class="fas fa-spinner fa-spin" style={{ color: '#0D826E' }}></i>:''} course{course.length > 1 ? 's' : ''}</h5>
                        <i class="fas fa-books text-danger fa-3x"></i>
                        <Link to='/dashboard/create-a-course'><button class="btn d-flex mx-auto" type="submit" ><b>Create a new course</b></button></Link>
                    </div>

            </div>
            
        </div>
            {course.length ?
            <section className="about container-fluid mx-auto" id="about">
                <div class="row">
                   
                    <small ><strong>Created Courses</strong></small>
                    {course.length  ? course.map((item, index) => {
                        return (
                            <>
                                <div class="col-md-4 mt-2" data-aos="fade-in" data-aos-delay="100">
                                    <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                                        <div class="d-flex justify-content-between">
                                            <div class="d-flex flex-row align-items-center">
                                                <div class="icon">  <i class="fab fa-html5 fa-2x" style={{ color: '#ffbb2c' }}></i> </div>
                                                <div class="ms-2 c-details">
                                                    <h6 class="mb-0">You</h6> <span><Moment fromNow>{item.created_at}</Moment></span>
                                                </div>
                                            </div>
                                            <div class="badge text-capitalize"> <span style={{ color: '#FFFFFF', backgroundColor: '#F75B00' }}>{item.status}</span> </div>
                                        </div>
                                        <div class="mt-2">
                                            <h6 class="heading">{item.course_title}</h6>
                                            <div class="mt-2">
                                                <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i> <i class="bi bi-star" style={{ color: '#071B4D' }}></i>
                                                <div class="mt-3"> <Link to={`/dashboard/created-courses/manage/${item.course_code}`} class="text2"><strong>Manage</strong></Link> </div>  </div>
                                        </div>
                                    </div>
                                </div>
                            </>
                        )
                    }) :''}



                </div>

            </section>:  
              <section className="about container-fluid mx-auto" id="about">
              <div class="row">
              <div class="col-md-4 mt-2">
                        <div class="card p-3 mb-2 shadow" style={{ borderRadius: '20px' }} >
                            <Skeleton count={5} />
                        </div>
                    </div>
                  </div>
                  </section>
            
            }
        </>
    )
}

export default CreatedCourses
