import React, { useEffect, useState } from 'react'
import Navbar from './navbar'
import { useParams } from 'react-router'
import axios from 'axios';
import { Baseurl } from '../baseurl';
import Skeleton from 'react-loading-skeleton';
import Footer from './footer';
import { useHistory } from 'react-router';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const Preview = () => {
  document.title = 'Fromhome | Course Preview'
  const history = useHistory();
  const { code } = useParams();
  const [sections, setSections] = useState([])
  const [videoFilePath, setVideoFilePath] = useState('');
  const fileUrl = `C:/xampp/htdocs/FROMHOME/backend/public/storage/`;
  const [note, setNote] = useState();
  const [show, setShow] = useState(false);
  const [showNote, setShowNote] = useState({})


  useEffect(() => {
    let token = sessionStorage.Token
    if (token) {
      axios.get(`${Baseurl}coursepreview`, {
        params: { code: code, token: token }
      }).then((res) => {
        console.log(res.data)
        if (res.data.sections) {
          setSections(res.data.sections)
        } else if (res.data.access) {
          history.push('/available-courses')
        }
      }).catch((error) => {
        if (error.response.status === 500) {
          history.push('/available-courses')
        }
      })
    }

  }, [])


  useEffect(() => {
    let token = sessionStorage.Token
    axios.get(`${Baseurl}getnote`, {
      params:{code:code},
      headers: {
        'Authorization': 'Bearer' + token
      }
    }).then((res)=>{
      if(res.data){
        setShowNote(res.data)
      }
    })
  }, [])

  const playVideo = (file) => {
    setVideoFilePath(file)
    console.log(fileUrl + videoFilePath)
  }

  const saveNote = () => {
    if (note) {
      let token = sessionStorage.Token
      setShow(true)
      axios.post(`${Baseurl}savenote`, {note: note, code:code}, {
        headers: {
          'Authorization': 'Bearer' + token
        }
      }).then((res) => {
        if(res.data === 'saved'){
          setShow(false)
          toast("üëç Note Saved", {
            autoClose: 2000,
          })
        }
      })
    } else {
      toast("‚úã Note Empty", {
        autoClose: 2000,
      })
    }
  }

  return (
    <>
      <Navbar />
      <section class="container-fluid mt-2">
      <small><ToastContainer /></small>
        <small><strong>üì∫ Playing, Best regards!</strong></small>
        <div class="row mx-auto">
          <div class="col-md-8 align-items-center vid-cont" data-aos="fade-in">
            <video width="100%" controls class="video mt-2 border" autoplay loop playsinline="true" webkit-playsinline="true" style={{ borderRadius: '10px' }}>
              <source src={fileUrl + videoFilePath} type="video/mp4" />
            </video>
          </div>

          <div class="col-md-4 bg-dark mt-2 px-0 accord-section" data-aos="fade-in">
            <small><strong>üìö Course Preview</strong></small>
            <ul class="nav nav-pills p-2 mb-3 justify-content-center" id="pills-tab" role="tablist">
              <li class="nav-item" role="presentation">
                <button class="nav-link active" id="pills-home-tab" data-bs-toggle="pill" data-bs-target="#pills-home" type="button" role="tab" aria-controls="pills-home" aria-selected="true"><small><strong>Sections</strong></small></button>
              </li>
              <li class="nav-item" role="presentation">
                <button class="nav-link" id="pills-profile-tab" data-bs-toggle="pill" data-bs-target="#pills-profile" type="button" role="tab" aria-controls="pills-profile" aria-selected="false"><small><strong>Note</strong></small></button>
              </li>
              <li class="nav-item" role="presentation">
                <button class="nav-link" id="pills-profile-tab" data-bs-toggle="pill" data-bs-target="#pills-review" type="button" role="tab" aria-controls="pills-profile" aria-selected="false"><small><strong>Review</strong></small></button>
              </li>
            </ul>


            <div class="tab-content" id="pills-tabContent">
              <div class="tab-pane fade show active" id="pills-home" role="tabpanel" aria-labelledby="pills-home-tab">

                {sections.length ? sections.map((item, index) => {
                  return (
                    <>
                      <div class="accordion" id="accordionExample">
                        <div class="accordion-item">
                          <h2 class="accordion-header" id="headingOne">
                            <button class="accordion-button fw-bold text-white border-0 rounded-0" type="button" data-bs-toggle="collapse" data-bs-target={`#${item.section_name.split(" ").join("")}`} aria-expanded="true" aria-controls="collapseOne" >
                              <small className='text-capitalize'>{item.section_name}</small>
                            </button>
                          </h2>
                          <div id={item.section_name.split(" ").join("")} class="accordion-collapse show collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                            <div class="accordion-body bg-secondary py-0">
                              {/* videos */}
                              <div class="row align-items-center course-part py-2">
                                {item.no_of_videos.map((video, index) => {
                                  return (
                                    <>
                                      <div class="col-2"><i class="fad fa-play" onClick={() => playVideo(video.video_url)}></i></div>
                                      <div class="col-7 text-wrap" key={index}><small className='text-capitalize'>{video.video_name}</small></div>
                                      <div class="col-3"><small><strong>{video.play_time}m</strong></small></div>
                                    </>
                                  )
                                })}
                              </div>
                              {/* attachements */}
                              {item.no_of_files.length ? <div class="row align-items-center course-part py-2">
                                <small style={{ color: '#0D826E' }}><strong><i class="fad fa-link"></i> Attachments</strong></small>
                                {item.no_of_files.map((file, index) => {
                                  return (
                                    <>
                                      <div class="col-2"><i class="fad fa-download"></i></div>
                                      <div class="col-7 text-wrap" key={index}><small className='text-capitalize'>Download Attachment</small></div>

                                    </>
                                  )
                                })}
                              </div> : ''}

                            </div>
                          </div>
                        </div>
                      </div>
                    </>
                  )
                }) : <div class="accordion mt-2"><Skeleton count={3} /></div>}
              </div>
              <div class="tab-pane fade" id="pills-profile" role="tabpanel" aria-labelledby="pills-profile-tab">
                <CKEditor
                  editor={ClassicEditor}
                  data={showNote}
                  onChange={(event, editor) => {
                    const data = editor.getData();
                    setNote(data)
                  }}
                />
                {showNote ? <>
                <small className='text-danger' style={{fontSize:'12px', cursor:'pointer'}}><i class="fad fa-trash"></i>
                <strong> Delete Note</strong></small>
                </>:''}
                <button class="btn d-flex mx-auto mb-2" onClick={saveNote} disabled={show}><b><small>{show?<i class="fas fa-spinner fa-spin"></i>:''} Save Note</small></b></button>
                
              </div>

              <div class="tab-pane fade" id="pills-review" role="tabpanel" aria-labelledby="pills-profile-tab">
                <form>
                  <div class="row-1">
                    <div class="row row-2"> <span id="login-card-inner">Drop a Review</span> </div>
                    <div class="row row-2">
                      <textarea />
                    </div>
                  </div>

                </form>
                <button class="btn d-flex mx-auto mb-2" type="submit"><b><small>Send Review</small></b></button>
              </div>

            </div>

          </div>

        </div>
      </section>
      <Footer />
    </>
  )
}

export default Preview
