import React from 'react'
import Footer from './footer'
import Navbar from './navbar'
import { Formik, Form, Field, ErrorMessage } from 'formik';

const AccountVerify = () => {
    return (
        <>
         <Navbar/> 
         <section className="about container-fluid mt-4" id="about">  
<div className='container-fluid'>
    <div className="row">
        <div className="col-md-6 col-sm mx-auto">
        <div class="login-card" style={{ borderRadius: '10px' }}>
              <div class="section-title">
                <h2><strong>Verify account</strong></h2>
              </div>
              <Formik
                initialValues={{ email: '', password: '' }}
                validate={values => {
                  const errors = {};
                  if (!values.email) {
                    errors.email = '*Email Required';
                  } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                  ) {
                    errors.email = 'Invalid email address';
                  } else if (!values.password) {
                    errors.password = '*Password Required';
                  }

                  return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                  setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    setSubmitting(false);
                  }, 400);
                }}
              >
                {({ isSubmitting }) => (

                  <Form>


                    <div class="row-1">
                      <div class="row row-2"> <span id="login-card-inner">Code</span> </div>
                      <div class="row row-2"> <Field type="email" placeholder="Enter verification code" name="email" />
                        <ErrorMessage name="email" component="small" className='text-danger' /> </div>
                    </div>

                    <button class="btn d-flex mx-auto" type="submit" disabled={isSubmitting}><b>Verify</b></button>
                  </Form>
                )}
              </Formik>
            </div>
        </div>

    </div>

</div>
</section>
         <Footer/>
        </>
    )
}

export default AccountVerify
